// Extract webhook data from body
const items = $input.all();
const webhookData = items[0].json.body;

// Standard fields that might exist
const standardFields = [
  'openHouseTitle',
  'openHouseType', 
  'submissionTimestamp',
  'guestPhotoUrl',
  'first_name',
  'last_name',
  'phone_number',
  'email',
  'are_you_currently_working_with_an_agent',
  'what_did_you_rate_the_house',
  'do_you_have_a_signed_buyer_agreement',
  'webhookUrl',
  'executionMode'
];

// Separate standard and custom fields
const standardData = {};
const customFields = {};

for (const [key, value] of Object.entries(webhookData)) {
  if (standardFields.includes(key)) {
    standardData[key] = value;
  } else {
    customFields[key] = value;
  }
}

// Format timestamp
const timestamp = standardData.submissionTimestamp ? 
  new Date(standardData.submissionTimestamp).toLocaleString('en-US', {
    dateStyle: 'full',
    timeStyle: 'short'
  }) : 'Not provided';

// Check if rating field exists
const hasRating = standardData.what_did_you_rate_the_house !== undefined && 
                  standardData.what_did_you_rate_the_house !== null &&
                  standardData.what_did_you_rate_the_house !== '';

const rating = hasRating ? parseInt(standardData.what_did_you_rate_the_house) : null;

// Determine lead priority based on agent status and rating (if available)
let leadPriority = 'MEDIUM';
let embedColor = 0xFFA500; // Orange for medium

const hasAgent = standardData.are_you_currently_working_with_an_agent?.toLowerCase();
const hasBuyerAgreement = standardData.do_you_have_a_signed_buyer_agreement?.toLowerCase();

// HOT lead: No agent and high rating (or no rating field but no agent)
if (hasAgent === 'no' && (!hasRating || rating >= 4)) {
  leadPriority = 'HOT';
  embedColor = 0xFF0000; // Red for hot
} 
// COLD lead: Has agent with agreement OR low rating
else if ((hasAgent === 'yes' && hasBuyerAgreement === 'yes') || (hasRating && rating <= 2)) {
  leadPriority = 'COLD';
  embedColor = 0x0099FF; // Blue for cold
}
// WARM lead: Has agent but no agreement
else if (hasAgent === 'yes' && hasBuyerAgreement === 'no') {
  leadPriority = 'WARM';
  embedColor = 0xFFA500; // Orange for warm
}

// Process base64 image
let imageBase64 = null;
let hasPhoto = false;
let imageMimeType = 'image/jpeg';

if (standardData.guestPhotoUrl && standardData.guestPhotoUrl.startsWith('data:image')) {
  try {
    // Extract mime type
    const mimeMatch = standardData.guestPhotoUrl.match(/data:(image\/[a-zA-Z]+);base64,/);
    if (mimeMatch) {
      imageMimeType = mimeMatch[1];
    }

    // Extract base64 data
    const base64Data = standardData.guestPhotoUrl.split(',')[1];
    imageBase64 = base64Data;
    hasPhoto = true;
  } catch (error) {
    console.log('Error processing image:', error.message);
  }
}

// Build custom fields display
const customFieldsArray = Object.entries(customFields)
  .filter(([key, value]) => value !== null && value !== undefined && value !== '')
  .map(([key, value]) => {
    const formattedKey = key.replace(/_/g, ' ')
      .split(' ')
      .map(word => word.charAt(0).toUpperCase() + word.slice(1))
      .join(' ');
    return { name: formattedKey, value: String(value), inline: true };
  });

return [{
  json: {
    // Core guest data
    firstName: standardData.first_name || 'Not provided',
    lastName: standardData.last_name || 'Not provided',
    fullName: `${standardData.first_name || ''} ${standardData.last_name || ''}`.trim() || 'Anonymous Guest',
    email: standardData.email || null,
    phone: standardData.phone_number || null,

    // Property info
    propertyAddress: standardData.openHouseTitle || 'Unknown Property',
    openHouseType: standardData.openHouseType || 'open-house',

    // Engagement data
    hasRating: hasRating,
    rating: rating,
    ratingDisplay: hasRating ? `${rating}/5` : 'Not rated',
    hasAgent: standardData.are_you_currently_working_with_an_agent || 'Not specified',
    hasBuyerAgreement: standardData.do_you_have_a_signed_buyer_agreement || null,
    timestamp: timestamp,
    rawTimestamp: standardData.submissionTimestamp,

    // Lead scoring
    leadPriority: leadPriority,
    embedColor: embedColor,

    // Image data
    hasPhoto: hasPhoto,
    imageBase64: imageBase64,
    imageMimeType: imageMimeType,

    // Custom fields
    customFields: customFieldsArray,
    hasCustomFields: customFieldsArray.length > 0,

    // Contact preferences
    hasEmail: !!standardData.email,
    hasPhone: !!standardData.phone_number,

    // Raw data for reference
    rawData: webhookData
  }
}];

