    // Add rating field only if it exists
    if ($json.hasRating) {
      fields.push({
        "name": "‚≠ê Rating",
        "value": $json.ratingDisplay,
        "inline": true
      });
    }

    // Add buyer agreement if available
    if ($json.hasBuyerAgreement) {
      fields.push({
        "name": "üìù Buyer Agreement",
        "value": $json.hasBuyerAgreement,
        "inline": true
      });
    }

    // Add contact info
    fields.push(
      {
        "name": "üìß Email",
        "value": $json.email || 'Not provided',
        "inline": true
      },
      {
        "name": "üì± Phone",
        "value": $json.phone || 'Not provided',
        "inline": true
      },
      {
        "name": "üïí Visit Time",
        "value": $json.timestamp,
        "inline": false
      }
    );

    // Add custom fields if any
    if ($json.customFields && $json.customFields.length > 0) {
      fields.push(...$json.customFields);
    }

    const payload = {
      "embeds": [
        {
          "title": "üè† New Open House Visitor - " + $json.leadPriority + " LEAD",
          "description": "**" + $json.fullName + "** visited **" + $json.propertyAddress + "**",
          "color": $json.embedColor,
          "fields": fields,
          "footer": {
            "text": "SignSnap Home | Lead Priority: " + $json.leadPriority
          },
          "timestamp": $json.rawTimestamp
        }
      ]
    };

    // Add thumbnail if photo exists (will use attachment)
    if ($json.hasPhoto) {
      payload.embeds[0].thumbnail = {
        "url": "attachment://" + $json.fullName.replace(/ /g, '_') + "_photo.jpg"
      };
    }

    return JSON.stringify(payload);
  })()
}}

