//
// üïò Automatic detection (uses server timezone or environment variable):
const LOCAL_TIMEZONE = $env.GENERIC_TIMEZONE || 'Europe/Paris';  // üü¢ Default fallback
// To use user-defined timezone instead, comment out the above line

// Local datetime in YYYY-MM-DD_HH-MM-SS format
const localDateTime = now.toLocaleString('sv-SE', { 
  timeZone: LOCAL_TIMEZONE,
  year: 'numeric',
  month: '2-digit',
  day: '2-digit',
  hour: '2-digit',
  minute: '2-digit',
  second: '2-digit'
}).replace(' ', '_').replace(/:/g, '-');

// Local formatted time for display
const localDateTimeFormatted = now.toLocaleString('sv-SE', { 
  timeZone: LOCAL_TIMEZONE,
  year: 'numeric',
  month: '2-digit',
  day: '2-digit',
  hour: '2-digit',
  minute: '2-digit',
  second: '2-digit'
});

// Local time HH:MM:SS format
const localTimeFormatted = now.toLocaleString('sv-SE', { 
  timeZone: LOCAL_TIMEZONE,
  hour: '2-digit',
  minute: '2-digit',
  second: '2-digit'
});

// Local date in YYYY-MM-DD format
const localDate = now.toLocaleDateString('sv-SE', { timeZone: LOCAL_TIMEZONE });

// Local time in French format
const localTimeFR = now.toLocaleString('sv-SE', { 
  timeZone: LOCAL_TIMEZONE,
  hour: '2-digit',
  minute: '2-digit'
}).replace(/:/g, 'h').replace(/^0(\d)h/, '$1h');

console.log('üöÄ USER-DEFINE TIMEZONE VARIABLES INITIALISED (Local timezone):');
console.log(`   üåç Timezone: ${LOCAL_TIMEZONE}`);
console.log(`   ‚ú® Now (UTC): ${now}`);
console.log(`   üìÖ Local date/time: ${localDateTime}`);
console.log(`   üïê Local time: ${localDateTimeFormatted}`);
