console.log(`   üìÅ Workflow name: ${WORKFLOW_NAME}`);
console.log(`   üìÅ Projects folder: ${N8N_PROJECTS_DIR}`);
console.log(`   üìÅ Project folder name: ${PROJECT_FOLDER_NAME}`);
