// üîç FIND LATEST BACKUP FOLDER
// YOUR_AWS_SECRET_KEY_HERE==

// Extract init data from previous node
const initData = $('Init').first().json;
const { timeData, workflowConfig, customConfig } = initData;

// Configuration
const BACKUP_FOLDER = customConfig.backupFolder;
const credentials = customConfig.credentials;

console.log(`üîç Searching for latest backup in: ${BACKUP_FOLDER}`);
console.log(`üìÅ Target credentials folder: ${credentials}`);

try {
  // Use Execute Command node before this one with: ls -1 /files/n8n-backups

  // If you have the directory list from previous node:
  const inputData = $input.first().json;

  // Assuming previous Execute Command node returns directory names
  let directories;

  if (inputData.stdout) {
    // Parse stdout from Execute Command node
    directories = inputData.stdout
      .split('\n')
      .filter(line => line.trim());
  } else {
    throw new Error('No directory listing found in input. Please use Execute Command node before this one.');
  }

  console.log(`üìÇ Found directories: ${directories.join(', ')}`);

  if (directories.length === 0) {
    throw new Error('No date-formatted directories found in backup folder');
  }

  // Sort directories by date (newest first)
  const sortedDirectories = directories.sort((a, b) => {
    // Convert YYYY-MM-DD to comparable format
    const dateA = new Date(a.trim());
    const dateB = new Date(b.trim());
    return dateB - dateA; // Descending order (newest first)
  });

  console.log(`üìÖ Sorted directories (newest first): ${sortedDirectories.join(', ')}`);

  // Get the most recent directory
  const latestBackupDate = sortedDirectories[0];
  const credentialsFullBackupPath = `${BACKUP_FOLDER}/${latestBackupDate}/${credentials}`;
  const workflowsFullBackupPath = `${BACKUP_FOLDER}/${latestBackupDate}/`;

  console.log(`‚úÖ Latest backup date: ${latestBackupDate}`);
  console.log(`‚úÖ Credentials' Full backup path: ${credentialsFullBackupPath}`);
  console.log(`‚úÖ Workflows'   Full backup path: ${workflowsFullBackupPath}`);

  // Return result
  const result = {
    backupFolder: BACKUP_FOLDER,
    credentialsFolder: credentials,
    latestBackupDate,
    credentialsFullBackupPath,
    workflowsFullBackupPath
  };

  return [{ json: result }];

} catch (error) {

  console.error(`‚ùå Error finding latest backup: ${error.message}`);
  throw new Error(`‚ùå Error finding latest backup: ${error.message}`);

}
