// Get the last execution timestamp from static data
const workflowStaticData = $getWorkflowStaticData('global');
const executionStart = $('Schedule Trigger').first().json.timestamp;

// At the end of successful execution, update the timestamp for future comparison
workflowStaticData.lastScheduleExecutionStarted = new Date(executionStart).getTime();

return [{ json: { lastScheduleExecutionStarted: workflowStaticData.lastScheduleExecutionStarted } }];

const recentlyChanged = [];
const workflowStaticData = $getWorkflowStaticData('global');
let lastScheduleExecutionStarted = workflowStaticData.lastScheduleExecutionStarted;

// Set fallback in case this is the first execution that should return all files found
if (!lastScheduleExecutionStarted) {
  lastScheduleExecutionStarted = new Date('2010-01-01').getTime();
}

// Compare created and update timestamps of each file to see if it's later than the last workflow execution
for (const item of $input.all()) {
  if ( (new Date(item.json.modifiedTime).getTime() > lastScheduleExecutionStarted ) || ( new Date(item.json.createdTime).getTime() > lastScheduleExecutionStarted ) ){
    recentlyChanged.push(item);
  }
}

return recentlyChanged;
