const out = [];

for (const input of $input.all()) {
  const data = input.json;

  // Map terms string to QuickBooks SalesTermRef value
  let termValue;
  if (data.terms) {
    const match = data.terms.match(/\d+/); // extract number from "Net 7"
    if (match) {
      termValue = match[0]; // e.g., "7"
    }
  }

  // Build QuickBooks Line array
  const Line = (data.products || []).map(p => ({
    DetailType: "SalesItemLineDetail",
    Amount: p.amount,
    Description: p.description || "",
    SalesItemLineDetail: {
      ItemRef: {
        value: p.qbItemId || p.itemId, // Must be QuickBooks Item ID
        name: p.itemName
      },
      Qty: p.qty || 1,
      UnitPrice: p.rate || 0
    }
  }));

  // ✅ Add discount line if discount exists
  if (data.discount) {
    Line.push({
      DetailType: "DiscountLineDetail",
      Amount: 0, // QuickBooks will calculate based on percentage
      DiscountLineDetail: {
        PercentBased: true,
        DiscountPercent: data.discount // only percent from previous output
      }
    });
  }

  // Invoice payload
  const invoicePayload = {
    CustomerRef: { value: String(data.customerId) },
    TxnDate: data.invoiceDate,
    DueDate: data.dueDate,
    PrivateNote: data.salesOrder,
    BillEmail: data.customerEmail
      ? { Address: data.customerEmail }
      : undefined,
    //SalesTermRef: termValue ? { value: termValue } : undefined,
    Line
  };

  out.push({ json: { customer: invoicePayload } });
}

return out;

// Input nodes
const salesOrders   = $items("Get Airtable Orders Records");        // Sales Order data
const customers     = $items("Get Customer Details");  // Customer details from Airtable
const products      = $items("Get Products");          // Product line items
const qbCustomers   = $items("Append Customers");      // QuickBooks customers
const searchProducts = $items("Search Product ID");    // Product ID lookup data

const result = [];

// Helper: find QuickBooks customer object by name
function getQuickBooksCustomer(name) {
  return qbCustomers.find(c => c.json.DisplayName === name);
}

// Helper: find Product ID by product/service name
function getQuickBooksProductId(name) {
  const match = searchProducts.find(p => p.json["(Q) Product/Service Name"] === name);
  return match ? match.json["(Q) Product ID"] : null;
}

for (const so of salesOrders) {
  const soData = so.json;

  // Ensure customer exists
  if (!soData["(Q) Customer"] || !Array.isArray(soData["(Q) Customer"]) || soData["(Q) Customer"].length === 0) {
    continue;
  }

  const customerIdFromSO = soData["(Q) Customer"][0];   // Airtable record id
  const matchedCustomer = customers.find(c => c.json.id === customerIdFromSO);

  if (matchedCustomer) {
    const customerName = matchedCustomer.json["(Q) Customer Name"];
    const qbCustomer = getQuickBooksCustomer(customerName);

    const quickBooksId = qbCustomer ? qbCustomer.json.Id : null;
    const customerEmail = qbCustomer?.json?.PrimaryEmailAddr?.Address || null; // ✅ fetch email

    // Find all products linked to this sales order
    const productMatches = products.filter(p =>
      p.json["Sales Order ID (A)"] &&
      p.json["Sales Order ID (A)"].includes(soData["Sales Order ID"])
    );

    const productDetails = productMatches.map(p => {
      const productName = (p.json["(Q) Item Name (A)"] && p.json["(Q) Item Name (A)"][0]) || "Unknown";
      return {
        itemId: getQuickBooksProductId(productName),
        itemName: productName,
        qty: p.json["(Q) Qty"] || 0,
        rate: p.json["(Q) Rate"] || 0,
        amount: p.json["(Q) Amount (A)"] || 0,
        description: (p.json["(Q) Description"] && p.json["(Q) Description"][0]) || ""
      };
    });

    result.push({
      customerId: quickBooksId,
      customerName: customerName,
      customerEmail: customerEmail, // ✅ include email here
      salesOrder: soData["Sales Order ID"],
      invoiceDate: soData["(Q) Invoice Date"],
      terms: soData["(Q) Terms"],
      dueDate: soData["(Q) Due Date (A)"],
      products: productDetails,
      discount: soData["(Q) Discount"]
    });
  }
}

// Return as items for n8n
return result.map(r => ({ json: r }));
