// n8n Code node: build HTML email for DEI eligibility notification

// Input data (replace with values from previous nodes or item fields)
const name = $json.Name || "Candidate";
const location = $json.Location || null;
const languages = $json.Language || null; // comma-delimited string
const gender = $json.Gender || null;
const disability = $json.Disability || null;
const deiEligible = ($json.DEI || "No").toLowerCase() === "yes";
const factor = $json.Factor || null;

// Email metadata (adjust as needed or set in Gmail node)
const subject = `DEI Eligibility ‚Äî ${name}`;
const preheader = `Candidate ${name} is ${deiEligible ? "eligible" : "not eligible"} for DEI hire${factor ? ` via ${factor}` : ""}.`;

// Build a safe text summary
const textSummaryParts = [
  `Candidate: ${name}`,
  `DEI Eligibility: ${deiEligible ? "Yes" : "No"}`,
];
if (factor) textSummaryParts.push(`Factor: ${factor}`);
if (location) textSummaryParts.push(`Location: ${location}`);
if (languages) textSummaryParts.push(`Language(s): ${languages}`);
textSummaryParts.push(`Gender: ${gender ?? "null"}`);
textSummaryParts.push(`Disability: ${disability ?? "null"}`);
const textBody = textSummaryParts.join("\n");

// Basic inline styles for email clients
const styles = {
  container: "font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Arial, sans-serif; color:#111; background:#ffffff; margin:0; padding:0;",
  wrapper: "max-width:640px; margin:0 auto; padding:24px;",
  card: "border:1px solid #e5e7eb; border-radius:12px; padding:20px;",
  h1: "font-size:20px; margin:0 0 12px; color:#111;",
  p: "margin:0 0 12px; line-height:1.5; color:#374151;",
  badgeYes: "display:inline-block; background:#065f46; color:#ffffff; font-weight:600; font-size:12px; padding:6px 10px; border-radius:999px;",
  badgeNo: "display:inline-block; background:#7f1d1d; color:#ffffff; font-weight:600; font-size:12px; padding:6px 10px; border-radius:999px;",
  table: "width:100%; border-collapse:collapse; margin-top:12px;",
  th: "text-align:left; font-size:12px; color:#6b7280; padding:6px 0;",
  td: "font-size:14px; color:#111; padding:6px 0;",
  footer: "margin-top:16px; font-size:12px; color:#6b7280;",
  divider: "height:1px; background:#e5e7eb; border:none; margin:16px 0;"
};

// Build HTML email
const html = `
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="utf-8" />
  <meta name="viewport" content="width=device-width,initial-scale=1" />
  <title>${subject}</title>
  <meta name="color-scheme" content="light only" />
  <meta name="supported-color-schemes" content="light" />
  <!-- Preheader text -->
  <span style="display:none !important; visibility:hidden; opacity:0; color:transparent; height:0; width:0; overflow:hidden;">${preheader}</span>
</head>
<body style="${styles.container}">
  <div style="${styles.wrapper}">
    <div style="${styles.card}">
      <h1 style="${styles.h1}">DEI Eligibility Review</h1>
      <p style="${styles.p}">
        Candidate <strong>${name}</strong> has been evaluated for DEI eligibility.
      </p>
      <p style="${styles.p}">
        Status:
        <span style="${deiEligible ? styles.badgeYes : styles.badgeNo}">
          ${deiEligible ? "DEI Eligible" : "Not DEI Eligible"}
        </span>
      </p>
      ${factor ? `<p style="${styles.p}">Primary eligibility factor: <strong>${factor}</strong>.</p>` : ""}

      <hr style="${styles.divider}" />

      <table role="presentation" style="${styles.table}">
        <tbody>
          <tr>
            <th style="${styles.th}">Name</th>
            <td style="${styles.td}">${name}</td>
          </tr>
          <tr>
            <th style="${styles.th}">Location</th>
            <td style="${styles.td}">${location ?? "‚Äî"}</td>
          </tr>
          <tr>
            <th style="${styles.th}">Language(s)</th>
            <td style="${styles.td}">${languages ?? "‚Äî"}</td>
          </tr>
          <tr>
            <th style="${styles.th}">Gender</th>
            <td style="${styles.td}">${gender ?? "null"}</td>
          </tr>
          <tr>
            <th style="${styles.th}">Disability</th>
            <td style="${styles.td}">${disability ?? "null"}</td>
          </tr>
          <tr>
            <th style="${styles.th}">DEI Eligible</th>
            <td style="${styles.td}">${deiEligible ? "Yes" : "No"}</td>
          </tr>
          ${factor ? `
          <tr>
            <th style="${styles.th}">Factor</th>
            <td style="${styles.td}">${factor}</td>
          </tr>` : ""}
        </tbody>
      </table>

      <p style="${styles.footer}">
        This notification is generated automatically from the latest evaluation records.
      </p>
    </div>
  </div>
</body>
</html>
`;

// Return for downstream Gmail node (use fields: subject, html, text)
return [
  {
    json: {
      subject,
      html,
      text: textBody
      // Optionally include "to" and "from" here or set them in the Gmail node configuration.
      // to: "user@example.com",
      // from: "user@example.com"
    }
  }
];
