const result = $input.all().map(item => {
  const body = item.json.body || {};
  const customer = body.customer || {};
  const defaultAddress = customer.default_address || {};
  const shippingAddress = body.shipping_address || {};
  const lineItem = (body.line_items && body.line_items[0]) || {};
  const totalTaxSet = body.total_tax_set?.shop_money || {};
  const totalDiscountSet = body.total_discounts_set?.shop_money || {};
  const totalShippingSet = body.total_shipping_price_set?.shop_money || {};

  return {
    customer_email: customer.email,
    customer_phone: customer.phone,
    created_date: body.created_at,
    updated_at: body.updated_at,
    name: body.name,
    customer_full_name: `${customer.first_name || ''} ${customer.last_name || ''}`.trim(),
    customer_id: customer.id,
    country_code: defaultAddress.country_code,
    customer_address: {
      country: defaultAddress.country,
      city: defaultAddress.city,
      zip: defaultAddress.zip,
      address1: defaultAddress.address1,
      address2: defaultAddress.address2
    },
    subtotal_price: body.subtotal_price,
    total_price: body.total_price,
    total_tax_amount: totalTaxSet.amount,
    total_discount_amount: totalDiscountSet.amount,
    total_shipping_amount: totalShippingSet.amount,
    shipping_address: {
      country: shippingAddress.country,
      city: shippingAddress.city,
      zip: shippingAddress.zip,
      address1: shippingAddress.address1,
      address2: shippingAddress.address2
    },
    line_item: {
      title: lineItem.title,
      sku: lineItem.sku,
      quantity: lineItem.quantity,
      vendor: lineItem.vendor,
      product_id: lineItem.product_id
    }
  };
});

return result;
