// Parse input string and calculate
const input = $input;
const expression = typeof input === 'string' ? input : JSON.stringify(input);

try {
  // Simple eval for basic math (in production, use a proper math parser)
  const result = eval(expression.replace(/[^0-9+\-*/().\s]/g, ''));
  return JSON.stringify({ result: result, expression: expression });
} catch (error) {
  return JSON.stringify({ error: 'Invalid mathematical expression', input: expression });
}
