// n8n Function node code
// Reads from Format Contract Data node and returns 1..3 items, each has .json with summary, description, start, end (YYYY-MM-DD)

const fmt = $node["Format Contract Data"].json;
const title = fmt["Title"] || "Untitled Contract";
const cid = fmt["Contract ID"] || "";
const parties = fmt["Parties"] || "";
const drive = fmt["Drive Link"] || "";

function isoDate(d) {
  // return YYYY-MM-DD
  return d.toISOString().split('T')[0];
}

function makeAllDayDates(dateStr) {
  // Accepts YYYY-MM-DD (or other parseable), returns start and end for all-day event.
  if(!dateStr) return null;
  const d = new Date(dateStr + "T00:00:00");
  const start = isoDate(d);
  const endD = new Date(d);
  endD.setDate(endD.getDate() + 1); // exclusive end for all-day events
  const end = isoDate(endD);
  return { start, end };
}

const items = [];

// Effective Date event
const eff = makeAllDayDates(fmt["Effective Date"]);
if (eff) {
  items.push({
    json: {
      summary: `${title} ‚Äî Effective Date`,
      description: `Contract ID: ${cid}\nParties: ${parties}\nDrive: ${drive}`,
      start: eff.start,
      end: eff.end,
      allDay: true,
      metaType: "effective"
    }
  });
}

// Termination Date event
const term = makeAllDayDates(fmt["Termination Date"]);
if (term) {
  items.push({
    json: {
      summary: `${title} ‚Äî Termination Date`,
      description: `Contract ID: ${cid}\nParties: ${parties}\nDrive: ${drive}`,
      start: term.start,
      end: term.end,
      allDay: true,
      metaType: "termination"
    }
  });

  // Renewal review reminder 60 days before termination
  const termDate = new Date(fmt["Termination Date"] + "T00:00:00");
  const reminderDate = new Date(termDate);
  reminderDate.setDate(reminderDate.getDate() - 60); // change 60 to your preferred days
  const remStart = isoDate(reminderDate);
  const remEndD = new Date(reminderDate);
  remEndD.setDate(remEndD.getDate() + 1);
  const remEnd = isoDate(remEndD);

  items.push({
    json: {
      summary: `${title} ‚Äî Renewal Review (Reminder)`,
      description: `Contract ID: ${cid}\nTermination Date: ${fmt["Termination Date"]}\nAction: Review & decide renewal.`,
      start: remStart,
      end: remEnd,
      allDay: true,
      metaType: "renewal_reminder"
    }
  });
}

return items;
