parameters.text:
=Analyze this lead and provide a qualification score:

Name: {{ $json.name }}
Email: {{ $json.email }}
Company: {{ $json.company }}
Message: {{ $json.message }}
Budget: {{ $json.budget }}
Timeline: {{ $json.timeline }}

parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "score": {
      "type": "integer",
      "description": "Lead qualification score from 0-100"
    },
    "category": {
      "type": "string",
      "enum": ["hot", "warm", "cold"],
      "description": "Lead temperature category"
    },
    "reasoning": {
      "type": "string",
      "description": "Brief explanation of the score"
    },
    "recommended_action": {
      "type": "string",
      "description": "Suggested next step"
    },
    "key_strengths": {
      "type": "array",
      "items": { "type": "string" },
      "description": "Top 3 positive indicators"
    },
    "concerns": {
      "type": "array",
      "items": { "type": "string" },
      "description": "Potential red flags or concerns"
    }
  },
  "required": ["score", "category", "reasoning", "recommended_action"]
}
