// Output: Only the video file URL

const results = [];

for (const item of items) {
  const html = item.json.data || "";

  // Regex to capture video .mp4 links
  const regex = /(https?:\/\/[^\s"']+\.mp4[^\s"']*)/gi;
  const matches = html.match(regex);

  if (matches && matches.length > 0) {
    results.push({ json: { videoUrl: matches[0] } }); // take first video link
  } else {
    results.push({ json: { videoUrl: null } });
  }
}

return results;

