const url = $input.first().json.message.text; // your input text

// Regex patterns
const fbRegex = /https?:\/\/([a-zA-Z0-9-]+\.)?facebook\.com\/[^\s]+/i;
const igRegex = /https?:\/\/([a-zA-Z0-9-]+\.)?instagram\.com\/[^\s]+/i;

// Match
const fbMatch = url.match(fbRegex);
const igMatch = url.match(igRegex);

return {
  json: {
    isFacebookLink: !!fbMatch,
    facebookUrl: fbMatch ? fbMatch[0] : null,
    isInstagramLink: !!igMatch,
    instagramUrl: igMatch ? igMatch[0] : null
  }
};
