const inputData = $input.all();
const formattedData = [];

const ownerAssignments = {
  owner1: "82654288",
  owner2: "944860204", // Replace with actual ID
};

// helper: safely unescape quotes if Jotform encodes them
const unescapeQuotes = (v) =>
  typeof v === 'string'
    ? v.replace(/\\'/g, "'").replace(/\\"/g, '"')
    : v;

for (const { json: item } of inputData) {
  // Extract and sanitize fields
  const firstName       = unescapeQuotes(item["First Name"] || item.first_name || '');
  const lastName        = unescapeQuotes(item["Last Name"] || item.last_name || '');
  const fullName        = `${firstName} ${lastName}`.trim() || 'Unknown';
  const email           = unescapeQuotes(item.Email || item.email || '');
  const linkedinProfile = unescapeQuotes(item["Linkedin Profile"] || item.linkedin || '');
  const companyName     = unescapeQuotes(item["Company Name"] || item.company_name || 'Unknown Company');
  const marketingBudget = unescapeQuotes(item["Marketing Budget ( in USD )"] || item.marketing_budget || '');
  const domain          = unescapeQuotes(item.Domain || item.domain || '');
  const specificQuery   = unescapeQuotes(item["Any Specific querry ?"] || item.query || '');

  // Task title
  const taskTitle = `(Jotform) Marketing Query from ${fullName} (${companyName})`;

  // Owner assignment (basic hash)
  const companyHash = (companyName || '').length % 2;
  const assignedOwnerId = companyHash === 0 ? ownerAssignments.owner1 : ownerAssignments.owner2;

  // Timestamp (24 hrs from now)
  const futureDate = new Date();
  futureDate.setHours(futureDate.getHours() + 24);
  const taskTimestamp = futureDate.toISOString();

  // Final formatted structure
  const formattedItem = {
    first_name: firstName,
    last_name: lastName,
    full_name: fullName,
    email: email,
    linkedin_profile: linkedinProfile,
    company_name: companyName,
    marketing_budget_usd: marketingBudget,
    domain: domain,
    specific_query: specificQuery,
    hubspot_task_title: taskTitle,
    task_timestamp: taskTimestamp,
    assigned_owner_id: assignedOwnerId,
  };

  formattedData.push({ json: formattedItem });
}

return formattedData;
