// Helpers
function isNonEmpty(x){ if(x===undefined||x===null) return false; if(typeof x==='number') return true; return String(x).trim()!==''; }
function prefer(...vals){ for(const v of vals){ if(isNonEmpty(v)) return v; } return ''; }
function safeNum(x, def=''){ const n = Number(x); return Number.isFinite(n) ? n : def; }

// Strip any leading bullets / dashes / numbers / checkboxes before we add our own bullets
function stripLeadingMarkers(s){
  return String(s).replace(/^\s*(?:[-*‚Ä¢\u2022]+|\d+[.)]|[‚úì‚úî‚úó‚úòxX\[\]\(\)])\s*/u, '');
}
function asArray(x){
  if(!x) return [];
  if(Array.isArray(x)) return x.filter(Boolean).map(v => stripLeadingMarkers(v));
  return String(x).split(/\r?\n+/).map(v => stripLeadingMarkers(v)).filter(Boolean);
}
function bullets(x){
  const a = asArray(x);
  return a.length ? a.map(s => `‚Ä¢ ${s}`).join('\n') : '';
}

// Current item from Merge
const i = $json;
// Originals straight from "Edit Fields" (for fields the merge might not include)
const src = $("Edit Fields").item?.json ?? {};

// ---- Ingest (preserve originals)
const timestamp           = prefer(i.timestamp, src.timestamp, new Date().toISOString());
const control_id          = prefer(i.control_id, src.control_id);
const control_description = prefer(i.control_description, src.control_description);
const response_text       = prefer(i.response_text, src.response_text);
const implementation_notes= prefer(i.implementation_notes, src.implementation_notes);
const evidence_url_1      = prefer(i.evidence_url_1, src.evidence_url_1);
const evidence_url_2      = prefer(i.evidence_url_2, src.evidence_url_2);
const evidence_url_3      = prefer(i.evidence_url_3, src.evidence_url_3);
const evidence_url_4      = prefer(i.evidence_url_4, src.evidence_url_4);

// ---- Scoring & mapping
const status       = prefer(i.status, 'Unknown');
const evaluation   = prefer(i.evaluation, status);
const score        = safeNum(i.score);
const confidence   = safeNum(i.confidence);
const rationaleIn  = prefer(i.rationale, i.reason);

// categories may be array or string
const categoriesStr = Array.isArray(i.categories) ? i.categories.join(', ') : prefer(i.categories);

// evidence: prefer i.evidence (array), else derive from URL fields, then filter empties
let evidenceArr = [];
if (Array.isArray(i.evidence)) {
  evidenceArr = i.evidence.filter(u => u && String(u).trim());
} else {
  evidenceArr = [evidence_url_1, evidence_url_2, evidence_url_3, evidence_url_4]
    .filter(u => u && String(u).trim());
}
const evidence_count = isNonEmpty(i.evidence_count) ? safeNum(i.evidence_count, evidenceArr.length) : evidenceArr.length;

// mapped requirements
const mappedReqs   = Array.isArray(i.mapped_requirements) ? i.mapped_requirements : [];
const mapped_count = isNonEmpty(i.mapped_count) ? safeNum(i.mapped_count, mappedReqs.length) : mappedReqs.length;
const mapping_flat = isNonEmpty(i.mapping_flat)
  ? String(i.mapping_flat)
  : mappedReqs.map(m => [m.framework, m.clause, m.title].filter(Boolean).join(': ')).join(' | ');

// frameworks selected (pretty commas)
const frameworks_selected = (isNonEmpty(i.frameworks_selected)
  ? String(i.frameworks_selected)
  : [...new Set(mappedReqs.map(m => m.framework).filter(Boolean))].join(', ')
).replace(/,\s*/g, ', ');

const engine_version = prefer(i.engine_version, i.version);

// ---- AI outputs (handle message.content object OR JSON string; also accept top-level)
let ai_summary = i.ai_summary;
let ai_findings_any = i.ai_findings;
let ai_recommendations_any = i.ai_recommendations;

if ((!ai_summary || (!ai_findings_any && !ai_recommendations_any)) && i.message?.content) {
  if (typeof i.message.content === 'object') {
    const c = i.message.content;
    ai_summary = ai_summary ?? c.ai_summary;
    ai_findings_any = ai_findings_any ?? c.ai_findings;
    ai_recommendations_any = ai_recommendations_any ?? c.ai_recommendations;
  } else {
    try {
      const parsed = JSON.parse(i.message.content);
      ai_summary = ai_summary ?? parsed.ai_summary;
      ai_findings_any = ai_findings_any ?? parsed.ai_findings;
      ai_recommendations_any = ai_recommendations_any ?? parsed.ai_recommendations;
    } catch {}
  }
}

// Normalize to single strings for the sheet (no double bullets)
const ai_findings        = bullets(ai_findings_any);
const ai_recommendations = bullets(ai_recommendations_any);

// Sync the item count inside rationale (e.g., replace "(4 items)" with "(3 items)")
let rationale = rationaleIn;
if (isNonEmpty(rationale)) {
  rationale = rationale.replace(/\(\s*\d+\s*items?\s*\)/i, `(${evidence_count} items)`);
}

// Synthesize summary if missing (deterministic)
if (!isNonEmpty(ai_summary)) {
  ai_summary = `Status: ${status}. Evaluation: ${evaluation}. Score: ${score}. `
             + `Confidence: ${confidence}. Evidence items: ${evidence_count}. `
             + `Categories: ${categoriesStr}. Mappings: ${mapping_flat}`;
}

// Return final row payload
return {
  json: {
    // Ingest
    timestamp,
    control_id,
    control_description,
    response_text,
    implementation_notes,
    evidence_url_1,
    evidence_url_2,
    evidence_url_3,
    evidence_url_4,

    // Scoring & mapping
    status,
    evaluation,
    score,
    confidence,
    rationale,
    categories: categoriesStr,
    evidence_count,
    mapped_count,
    mapping_flat,
    frameworks_selected,
    engine_version,

    // AI (strings)
    ai_summary,
    ai_findings,
    ai_recommendations
  }
};
