function tranformContent(content) {
  return [].concat(content).map(content => {
    if (typeof content === 'string') {
      return { type: "input_text", text: content };
    }
    return {
      type: getInputType(content.type),
      [content.type]: content[content.type].url
    }
  })
};

function getInputType(type) {
  if (type === 'image_url') return 'input_image';
  if (type === 'file_url') return 'input_file';
  return 'input_text';
}

const input = $input.item.json.body.messages.map(message => ({
  role: message.role,
  content: tranformContent(message.content)
}));

return { input };

