const baseUrl = "https://www.film.ru";
const latest = items[0].json;

// Clearing the link string
const cleanLink = latest.link.match(/\[([^\]]+)\]/)[1];
// Clearing the image line
const cleanImage = latest.image.match(/\[([^\]]+)\]/)[1];

return [{
  json: {
    image: baseUrl + cleanImage,
    fullUrl: baseUrl + cleanLink
  }
}];

// Getting all the input elements
const items = $input.all();

// Processing each element
return items.map(item => {
  const text = item.json.Text;
  // Deleting all occurrences of [ ... ]
  const cleanText = text.replace(/\[[^\]]*\]/g, '');

  // Returning the object with the cleared text
  return {
    json: {
      cleanText: cleanText,
    },
  };
});
