const input = $input.first().json.data
const regex = /https?:\/\/[^\/\s"'>]+/g
const websites = input.match(regex)
return websites.map(website => ({json:{website}}))

// Array of start indices for pagination
const starts = [ 1, 11, 21, 31];

// Create an array of items, one per start value
return starts.map(s => ({ json: { start: s } }));

// Each input item from HTTP Request node
return $input.all().map(item => {
  // Extract only the 'items' array from the API response
  const results = item.json.items || [];

  // Return each search result as a separate item
  return results.map(r => ({
    json: r
  }));
}).flat();

// For each input item (already a search result)
return $input.all().map(item => {
    const r = item.json;
    const metatags = (r.pagemap && r.pagemap.metatags && r.pagemap.metatags[0]) || {};

    return {
        json: {
            "Business Name": r.title || null,
            "Email ID": metatags['og:email'] || null,
            "Contact Number": metatags['og:phone_number'] || null,
            "URL": r.link || null,
            "Description": r.snippet || metatags['og:description'] || null,
            "Type": metatags['og:type'] || null,
            "Socials": metatags['og:see_also'] || null
        }
    };
});

// Get current batch of items
const items = $input.all();

// Regex patterns
const emailRegex = /[a-zA-Z0-9._%+-]+@[a-zA-Z0-9.-]+\.[a-z]{2,}/g;
const phoneRegex = /(?:\+?\d{1,2}\s?)?(?:\(\d{3}\)|\d{3})[-.\s]?\d{3}[-.\s]?\d{4}/g;
const socialRegex = /(https?:\/\/(?:www\.)?(?:facebook|twitter|instagram|linkedin|youtube)\.com\/[^\s"'<>]+)/gi;

// Helper to remove duplicates
function uniqueArray(arr) {
    return arr ? [...new Set(arr)] : [];
}

// Function to extract data
function extractFromHTML(html) {
    const emails = uniqueArray(html.match(emailRegex));
    const phones = uniqueArray(html.match(phoneRegex));
    const socials = uniqueArray(html.match(socialRegex));

    return {
        email1: emails.length > 0 ? emails[0] : null,
        email2: emails.length > 1 ? emails.slice(1).join(", ") : null, // all remaining emails
        phone: phones.length > 0 ? phones.join(", ") : null, // string instead of array
        socials: socials.length > 0 ? socials.join(", ") : null // string instead of array
    };
}

// Process each item
const output = items.map(item => {
    const htmlData = item.json.data || ""; // assuming HTML content is in 'data'
    const extracted = extractFromHTML(htmlData);

    return { json: extracted };
});

return output;

const item = items[0];
const chatInput = item.json.chatInput;
const formattedQuery = chatInput.replaceAll(' ', '+');

return [{
  json: {
    "searchQuery": formattedQuery
  }
}];

// --- Safety Check (Guard Clause) ---
if (!items[0] || !items[0].json || !items[0].json.data) {
  return [{
    json: {
      error: "Input data from the previous node is missing or in the wrong format.",
      businessName: null,
      description: null,
      phone: null,
      socials: null,
      email1: null,
      email2: null,
      URL: null
    }
  }];
}

const htmlString = items[0].json.data;

// --- Business Name Extraction ---
let businessName = "";
const jsonLdRegex = /<script[^>]*type=["']?application\/ld\+json["']?[^>]*>([\s\S]*?)<\/script>/gi;
const jsonLdMatches = [...htmlString.matchAll(jsonLdRegex)];
for (const match of jsonLdMatches) {
    try {
        const jsonContent = JSON.parse(match[1]);
        if (!businessName) {
            if (jsonContent.name && (jsonContent['@type'] === 'Dentist' || jsonContent['@type'] === 'Organization')) {
                businessName = jsonContent.name;
            } else if (Array.isArray(jsonContent['@graph'])) {
                for (const item of jsonContent['@graph']) {
                    if (item.name && (item['@type'] === 'Dentist' || item['@type'] === 'WebSite')) {
                        businessName = item.name.replace(/ New York New York$/, '').trim();
                        break;
                    }
                }
            }
        }
    } catch (e) { /* Ignore */ }
}
if (!businessName) {
    const ogTitleRegex = /<meta[^>]*property=["']?og:title["']?[^>]*content=["']?([^"'>]*)["']?/i;
    const ogTitleMatch = htmlString.match(ogTitleRegex);
    if (ogTitleMatch && ogTitleMatch[1]) {
        businessName = ogTitleMatch[1].split(/[-|]/)[0].trim();
    }
}
if (!businessName) {
    const titleRegex = /<title>([\s\S]*?)<\/title>/i;
    const titleMatch = htmlString.match(titleRegex);
    if (titleMatch && titleMatch[1]) {
      businessName = titleMatch[1].split(/[-|]/)[0].trim();
    }
}

// --- Other Data Extraction ---
const descriptionRegex = /<meta[^>]*name=["']?description["']?[^>]*content=["']?([^"'>]*)["']?/i;
const descriptionMatch = htmlString.match(descriptionRegex);
const description = descriptionMatch ? descriptionMatch[1].trim() : "";

const phoneRegex = /(?:\+?1[ -]?)?\(?\d{3}\)?[-.\s]?\d{3}[-.\s]?\d{4}/g;
const foundPhones = htmlString.match(phoneRegex) || [];
const phoneNumbers = [...new Set(foundPhones)];

function findSameAsLinks(obj, links = []) {
    if (obj === null || typeof obj !== 'object') return links;
    if (obj.sameAs && Array.isArray(obj.sameAs)) {
        links.push(...obj.sameAs.filter(item => typeof item === 'string'));
    }
    Object.values(obj).forEach(value => {
        if (typeof value === 'object') findSameAsLinks(value, links);
    });
    return links;
}
let jsonLdLinks = [];
for (const match of jsonLdMatches) {
  try {
    const jsonContent = JSON.parse(match[1]);
    findSameAsLinks(jsonContent, jsonLdLinks);
  } catch(e) {/* Ignore */}
}
const socialAnchorRegex = /href=["']?(https?:\/\/(?:www\.)?(?:facebook|instagram|twitter|linkedin|yelp|pinterest|plus\.google)\.com\/[^"'\s>]+)/gi;
const socialAnchorLinks = [...htmlString.matchAll(socialAnchorRegex)].map(match => match[1]);
const allSocials = [...jsonLdLinks, ...socialAnchorLinks];
const socials = [...new Set(allSocials)];

const plainTextEmailRegex = /\b[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}\b/gi;
const plainTextEmails = htmlString.match(plainTextEmailRegex) || [];
const mailtoRegex = /href=["']?mailto:([^"'\?\s>]+)/gi;
const mailtoEmails = [...htmlString.matchAll(mailtoRegex)].map(match => match[1]);
const allEmails = [...plainTextEmails, ...mailtoEmails];
const emails = [...new Set(allEmails)];

// --- Final Output Formatting ---
const socialString = socials.join(', ');
const phoneString = phoneNumbers.join(', ');

const email1 = emails[0] || null;
const email2 = emails[1] || null;

// **UPDATED:** Get the URL and add a trailing slash if needed.
let websiteUrl = $('Loop Over Items').first().json.website;
if (websiteUrl && typeof websiteUrl === 'string' && !websiteUrl.endsWith('/')) {
  websiteUrl += '/';
}

return [{
  json: {
    businessName,
    description,
    phone: phoneString,
    socials: socialString,
    email1,
    email2,
    URL: websiteUrl,
  }
}];
