// n8n Code Node - Compute KPI Trends
// This code analyzes ClickUp tasks and lead data to generate KPIs

const items = $input.all();
const data = items.map(item => item.json);

// Separate tasks and leads
const tasks = data.filter(item => item.id && item.id.startsWith('86d0'));
const leads = data.filter(item => item.leadId);

// Initialize KPI object
const kpis = {
  timestamp: new Date().toISOString(),
  tasks: {},
  leads: {},
  overall: {}
};

// ===== TASK KPIs =====
if (tasks.length > 0) {
  // Status distribution
  const statusCount = {};
  const priorityCount = {};
  const assigneeCount = {};
  let totalTimeSpent = 0;
  let tasksWithTime = 0;
  let overdueTasks = 0;
  const today = Date.now();

  tasks.forEach(task => {
    // Status counting
    const status = task.status?.status || 'unknown';
    statusCount[status] = (statusCount[status] || 0) + 1;

    // Priority counting
    const priority = task.priority?.priority || 'none';
    priorityCount[priority] = (priorityCount[priority] || 0) + 1;

    // Assignee counting
    task.assignees?.forEach(assignee => {
      assigneeCount[assignee.username] = (assigneeCount[assignee.username] || 0) + 1;
    });

    // Time tracking
    if (task.time_spent) {
      totalTimeSpent += task.time_spent;
      tasksWithTime++;
    }

    // Overdue calculation
    if (task.due_date && parseInt(task.due_date) < today && !task.date_done) {
      overdueTasks++;
    }
  });

  kpis.tasks = {
    total: tasks.length,
    byStatus: statusCount,
    byPriority: priorityCount,
    byAssignee: assigneeCount,
    overdue: overdueTasks,
    overduePercentage: ((overdueTasks / tasks.length) * 100).toFixed(2),
    avgTimeSpent: tasksWithTime > 0 ? Math.round(totalTimeSpent / tasksWithTime) : 0,
    avgTimeSpentHours: tasksWithTime > 0 ? (totalTimeSpent / tasksWithTime / 3600000).toFixed(2) : 0,
    completion: {
      done: statusCount['done'] || 0,
      inProgress: statusCount['in progress'] || 0,
      finalReview: statusCount['final review'] || 0,
      backlogs: statusCount['backlogs'] || 0
    }
  };
}

// ===== LEAD KPIs =====
if (leads.length > 0) {
  const sourceCount = {};
  const statusCount = {};
  const responsesByDate = {};
  let positiveResponses = 0;
  let negativeResponses = 0;
  let neutralResponses = 0;

  // Keywords for sentiment analysis
  const positiveKeywords = ['interested', 'love', 'promising', 'sounds interesting', 'count me in', 'yes', 'great'];
  const negativeKeywords = ['not interested', 'not sure', 'concerns', 'difficult', 'already have'];

  leads.forEach(lead => {
    // Source counting
    const source = lead.source || 'unknown';
    sourceCount[source] = (sourceCount[source] || 0) + 1;

    // Status counting
    const status = lead.status || 'unknown';
    statusCount[status] = (statusCount[status] || 0) + 1;

    // Date grouping
    const date = lead.replyDate || 'unknown';
    responsesByDate[date] = (responsesByDate[date] || 0) + 1;

    // Simple sentiment analysis
    if (lead.reply) {
      const replyLower = lead.reply.toLowerCase();
      const hasPositive = positiveKeywords.some(kw => replyLower.includes(kw));
      const hasNegative = negativeKeywords.some(kw => replyLower.includes(kw));

      if (hasPositive && !hasNegative) {
        positiveResponses++;
      } else if (hasNegative && !hasPositive) {
        negativeResponses++;
      } else {
        neutralResponses++;
      }
    }
  });

  kpis.leads = {
    total: leads.length,
    bySource: sourceCount,
    byStatus: statusCount,
    byDate: responsesByDate,
    sentiment: {
      positive: positiveResponses,
      negative: negativeResponses,
      neutral: neutralResponses,
      positiveRate: ((positiveResponses / leads.length) * 100).toFixed(2),
      negativeRate: ((negativeResponses / leads.length) * 100).toFixed(2)
    },
    topSources: Object.entries(sourceCount)
      .sort((a, b) => b[1] - a[1])
      .slice(0, 3)
      .map(([source, count]) => ({ source, count }))
  };
}

// ===== OVERALL KPIs =====
kpis.overall = {
  totalItems: data.length,
  tasksCount: tasks.length,
  leadsCount: leads.length,
  dataQuality: {
    tasksWithDueDate: tasks.filter(t => t.due_date).length,
    tasksWithAssignees: tasks.filter(t => t.assignees?.length > 0).length,
    leadsWithCompany: leads.filter(l => l.company).length,
    leadsWithPhone: leads.filter(l => l.phone).length
  }
};

// ===== TREND INDICATORS =====
kpis.trends = {
  taskTrend: tasks.length > 0 ? 'stable' : 'no data',
  leadTrend: leads.length > 5 ? 'active' : 'low activity',
  urgentTasks: tasks.filter(t => t.priority?.priority === 'urgent').length,
  newReplies: leads.filter(l => l.status === 'New Reply').length
};

// Return as single item with comprehensive KPI data
return [{ json: kpis }];

