// Input: items array (from Gmail node output)
// Output: one item per failed email address

return items
  .map(item => {
    const snippet = item.json.snippet || "";
    const match = snippet.match(/wasn't delivered to\s+([\w.-]+@[\w.-]+\.\w+)/i);
    if (match) {
      return {
        json: {
          failedEmail: match[1],
          subject: item.json.Subject || "",
          id: item.json.id,
          snippet: snippet
        }
      };
    }
    return null;
  })
  .filter(item => item !== null);

