// Parcours tous les items re√ßus
return items.map(item => {
  // R√©cup√©ration des valeurs depuis l'item
  const completionTokens = item.json.completionTokens || 0;
  const promptTokens = item.json.promptTokens || 0;
  const completionTokensPrice = item.json.completionTokensPrice || 0;
  const promptTokensPrice = item.json.promptTokensPrice || 0;

  // Calcul du co√ªt global
  const globalCost = (completionTokens * completionTokensPrice) + (promptTokens * promptTokensPrice);

  // Retourner l'item avec globalCost ajout√©
  return {
    json: {
      ...item.json,
      globalCost
    }
  };
});

// === Calcul des KPI pour les conversations ===
let totalConversations = 0;
let totalCompletionTokens = 0;
let totalPromptTokens = 0;
let totalTokens = 0;
let totalCost = 0;
let sessions = new Set();
let modelUsage = {};
let conversationsParJour = [];

for (const item of items) {
  const data = item.json;

  totalConversations += 1;
  totalCompletionTokens += data.completionTokens || 0;
  totalPromptTokens += data.promptTokens || 0;
  totalTokens += data.totalTokens || 0;

  // Calcul du co√ªt global si absent
  let globalCost = data.globalCost;
  if (globalCost === null || globalCost === undefined) {
    const completionTokensPrice = data.completionTokensPrice || 0;
    const promptTokensPrice = data.promptTokensPrice || 0;
    globalCost = (data.completionTokens || 0) * completionTokensPrice + (data.promptTokens || 0) * promptTokensPrice;
  }
  totalCost += globalCost;

  // Comptage des sessions uniques
  if (data.sessionId) sessions.add(data.sessionId);

  // Comptage messages par mod√®le
  const model = data.modelName || "unknown";
  if (!modelUsage[model]) modelUsage[model] = 0;
  modelUsage[model] += 1;

  // Tableau journalier
  if (data.createdAt) {
    const day = data.createdAt.split("T")[0];
    let dayEntry = conversationsParJour.find(d => d.date === day);
    if (!dayEntry) {
      dayEntry = { date: day, count: 0, totalCost: 0, promptTokens: 0, completionTokens: 0 };
      conversationsParJour.push(dayEntry);
    }
    dayEntry.count += 1;
    dayEntry.totalCost += globalCost;
    dayEntry.promptTokens += data.promptTokens || 0;
    dayEntry.completionTokens += data.completionTokens || 0;
  }
}

// Tri par date
conversationsParJour = conversationsParJour.sort((a, b) => a.date.localeCompare(b.date));

// Moyennes
const avgTokensPerConversation = totalConversations > 0 ? (totalTokens / totalConversations).toFixed(2) : 0;
const avgCostPerConversation = totalConversations > 0 ? (totalCost / totalConversations).toFixed(6) : 0;

