parameters.jsCode:
const items = $input.all();

for (const item of items) {
  // Check for output from the AI Agent
  if (item.json.output && item.json.output.post && item.json.output.post.text) {

    const rawText = item.json.output.post.text;

    // List of punctuation characters that can end a sentence
    const punctuation = ['.', '!', '?', '"', '‚ú®', '‚òïÔ∏è'];
    let lastPuncIndex = -1;

    // Find the last position of each punctuation mark and determine the final one
    for (const punc of punctuation) {
      lastPuncIndex = Math.max(lastPuncIndex, rawText.lastIndexOf(punc));
    }

    // If a sentence-ending character is found
    if (lastPuncIndex !== -1) {
      // Trim the text up to and including that character, overwriting the original
      item.json.output.post.text = rawText.substring(0, lastPuncIndex + 1);
    }
    // If not found, do nothing (leave the original text as is)
  }
}

// Return the items with the formatted data to the next node
return items;
