const item = $input.first().json;

try {
  const response = item.choices[0].message.content;
  let cleanJson = response.replace(/```json\n?|```\n?/g, '').trim();
  const jsonMatch = cleanJson.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    cleanJson = jsonMatch[0];
  }
  const evaluationData = JSON.parse(cleanJson);
  const originalData = $('Extract Application Data').first().json;
  return [{
    json: {
      ...originalData,
      ...evaluationData
    }
  }];
} catch (error) {
  const originalData = $('Extract Application Data').first().json;
  const gpa = parseFloat(originalData.gpa) || 0;
  const satScore = parseInt(originalData.sat_score) || 0;
  let score = 0;
  if (gpa >= 3.8) score += 40;
  else if (gpa >= 3.5) score += 30;
  else if (gpa >= 3.0) score += 20;
  else score += 10;
  if (satScore >= 1400) score += 40;
  else if (satScore >= 1200) score += 30;
  else if (satScore >= 1000) score += 20;
  else score += 10;
  score += 20;
  let decision = 'reject';
  if (score >= 90) decision = 'auto_accept';
  else if (score >= 70) decision = 'interview_required';
  return [{
    json: {
      ...originalData,
      eligibility_score: score,
      decision_category: decision,
      academic_strength: gpa >= 3.5 ? 'strong' : 'average',
      scholarship_eligible: score >= 85,
      estimated_scholarship: score >= 85 ? 10000 : 0,
      admission_likelihood: score >= 80 ? 'high' : score >= 60 ? 'medium' : 'low'
    }
  }];
}
