/**
 * n8n Code node
 * Input:  items where each item.json = { chunk: string, order: string }
 * Output: array of payload objects with primary, proceeding, and following sections
 */

const inputItems = $input.all();

// If a single item contains the whole array under a property (e.g. { data: [...] }),
// you can adapt the next line accordingly.
// Example: const chunks = inputItems[0]?.json?.data ?? inputItems.map(item => item.json);
const chunks = inputItems.map(item => item.json);

// Sort by numeric order to ensure correct sequence
chunks.sort((a, b) => Number(a.order) - Number(b.order));

const results = chunks.map((primaryChunk, index) => {
  const prevChunk = chunks[index - 1] ?? null;
  const nextChunk = chunks[index + 1] ?? null;

  const payload = [
    '### Primary ###',
    '---',
    primaryChunk.chunk ?? '',
    '---',
    '### Proceeding ###',
    '---',
    prevChunk ? prevChunk.chunk ?? '' : '',
    '---',
    '### Following ###',
    '---',
    nextChunk ? nextChunk.chunk ?? '' : '',
  ].join('\n');

  return {
    json: {
      payload,
      'primary-order': primaryChunk.order ?? '',
      'proceeding-order': prevChunk?.order ?? '',
      'following-order': nextChunk?.order ?? '',
      'payload-order': String(index + 1),
    },
  };
});

return results;

// n8n Function node code
const H1_LIMIT = 4000;
const H2_LIMIT = 5000;

function splitByHeading(text, level) {
  const src = String(text ?? '');
  if (!src.trim()) return [];

  const lines = src.split(/\r?\n/);
  const headingIdxs = [];
  let inFence = null;

  const fenceRe = /^\s*(`{3,}|~{3,})/;
  const headingRe = /^[ \t]{0,3}(#{1,6})[ \t]+(.+?)\s*#*\s*$/;

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i];

    const fm = line.match(fenceRe);
    if (fm) {
      const fenceChar = fm[1][0];
      if (!inFence) inFence = fenceChar;
      else if (inFence === fenceChar) inFence = null;
      continue;
    }

    if (!inFence) {
      const hm = line.match(headingRe);
      if (hm && hm[1].length === level) {
        headingIdxs.push(i);
      }
    }
  }

  if (headingIdxs.length === 0) {
    const only = lines.join('\n').trim();
    return only ? [only] : [];
  }

  const sections = [];
  for (let k = 0; k < headingIdxs.length; k++) {
    const start = headingIdxs[k];
    const end = (k + 1 < headingIdxs.length) ? headingIdxs[k + 1] : lines.length;

    let sectionLines = lines.slice(start, end);

    if (k === 0 && start > 0) {
      sectionLines = lines.slice(0, start).concat(sectionLines);
    }

    const section = sectionLines.join('\n').trim();
    if (section) sections.push(section);
  }

  return sections;
}

const rawInput = $json.output ?? '';
const text = typeof rawInput === 'string' ? rawInput : String(rawInput);

const finalChunks = [];
const h1Sections = splitByHeading(text, 1);
const topLevelSections = h1Sections.length ? h1Sections : [text.trim()].filter(Boolean);

for (const h1 of topLevelSections) {
  if (h1.length <= H1_LIMIT) {
    finalChunks.push(h1);
    continue;
  }

  const h2Sections = splitByHeading(h1, 2);
  const secondLevelSections = h2Sections.length ? h2Sections : [h1];

  for (const h2 of secondLevelSections) {
    if (h2.length <= H2_LIMIT) {
      finalChunks.push(h2);
      continue;
    }

    const h3Sections = splitByHeading(h2, 3);
    if (h3Sections.length) {
      finalChunks.push(...h3Sections);
    } else {
      finalChunks.push(h2);
    }
  }
}

return finalChunks.map((chunk, idx) => ({
  json: {
    chunk,
    order: String(idx + 1),
  },
}));

const crypto = require('crypto');

function encodeRfc3986(str) {
  return encodeURIComponent(str).replace(/[!'()*]/g, c =>
    '%' + c.charCodeAt(0).toString(16).toUpperCase()
  );
}

function encodePath(key) {
  return key.split('/').map(encodeRfc3986).join('/');
}

function awsS3PresignDownload({
  accessKeyId,
  secretAccessKey,
  bucket,
  region,
  key,
  expires = 900,
  endpointHost,
}) {
  if (!key || /^https?:\/\//i.test(key)) {
    throw new Error('key must be the S3 object key (e.g., "folder/file.txt"), not a full URL.');
  }

  const host = endpointHost
    ? endpointHost
    : (region === 'us-east-1'
        ? `${bucket}.s3.amazonaws.com`
        : `${bucket}.s3.${region}.amazonaws.com`);

  const now = new Date();
  const pad = n => n.toString().padStart(2, '0');
  const date = `${now.getUTCFullYear()}${pad(now.getUTCMonth() + 1)}${pad(now.getUTCDate())}`;
  const amzDate = `${date}T${pad(now.getUTCHours())}${pad(now.getUTCMinutes())}${pad(now.getUTCSeconds())}Z`;

  const algorithm = 'AWS4-HMAC-SHA256';
  const scope = `${date}/${region}/s3/aws4_request`;
  const signedHeaders = 'host';

  const canonicalUri = `/${encodePath(key)}`;
  const xAmzParams = {
    'X-Amz-Algorithm': algorithm,
    'X-Amz-Credential': `${accessKeyId}/${scope}`,
    'X-Amz-Date': amzDate,
    'X-Amz-Expires': String(expires),
    'X-Amz-SignedHeaders': signedHeaders,
  };

  const canonicalQuery = Object.keys(xAmzParams)
    .sort()
    .map(k => `${encodeRfc3986(k)}=${encodeRfc3986(xAmzParams[k])}`)
    .join('&');

  const canonicalHeaders = `host:${host}\n`;
  const canonicalRequest = [
    'GET',
    canonicalUri,
    canonicalQuery,
    canonicalHeaders,
    signedHeaders,
    'UNSIGNED-PAYLOAD',
  ].join('\n');

  const hashedRequest = crypto.createHash('sha256').update(canonicalRequest, 'utf8').digest('hex');
  const stringToSign = `${algorithm}\n${amzDate}\n${scope}\n${hashedRequest}`;

  const hmac = (key, data) => crypto.createHmac('sha256', key).update(data, 'utf8').digest();
  const kDate = hmac(`AWS4${secretAccessKey}`, date);
  const kRegion = hmac(kDate, region);
  const kService = hmac(kRegion, 's3');
  const kSigning = hmac(kService, 'aws4_request');

  const signature = crypto.createHmac('sha256', kSigning).update(stringToSign, 'utf8').digest('hex');

  return `https://${host}${canonicalUri}?${canonicalQuery}&X-Amz-Signature=${signature}`;
}

// Configuration
const api_key = 'XXXXXX';      
const secret_key = 'YYYYYYYYYYYYYYYYY';        
const bucket_name = 'n8n-AWS-Bucket-Name-Here';
const region = 'us-west-2';

// Get data from the Download file1 node (which has the binary data with fileName)
const downloadedFiles = $('Download Document to Blockify').all();

// Process each item
const results = [];

for (const item of downloadedFiles) {
  let fileName = null;

  // Get filename from binary data
  if (item.binary && item.binary.data && item.binary.data.fileName) {
    fileName = item.binary.data.fileName;
  } else if (item.binary) {
    // Try to find it in any binary property
    const binaryKeys = Object.keys(item.binary);
    for (const key of binaryKeys) {
      if (item.binary[key] && item.binary[key].fileName) {
        fileName = item.binary[key].fileName;
        break;
      }
    }
  }

  // Fallback to json.name if binary fileName not found
  if (!fileName && item.json && item.json.name) {
    fileName = item.json.name;
  }

  if (!fileName) {
    throw new Error(`Cannot find fileName. Binary keys: ${item.binary ? JSON.stringify(Object.keys(item.binary)) : 'no binary'}, JSON keys: ${JSON.stringify(Object.keys(item.json))}`);
  }

  // Clean the filename (remove leading slashes if any)
  const cleanFileName = fileName.replace(/^\/+/, '');

  // Construct the S3 key (matches the upload configuration with inputs folder)
  const s3Key = `inputs/${cleanFileName}`;

  const url = awsS3PresignDownload({
    accessKeyId: api_key,
    secretAccessKey: secret_key,
    bucket: bucket_name,
    region: region,
    key: s3Key,
    expires: 8400,
  });

  results.push({
    json: {
      url: url,
      fileName: cleanFileName,
      s3Key: s3Key
    }
  });
}

return results;
