// Parse form data and prepare for processing
const formData = $input.first().json;

return {
  json: {
    submissionId: formData.submissionID || Date.now().toString(),
    employeeName: formData.employeeName || formData.q3_employeeName,
    employeeEmail: formData.employeeEmail || formData.q4_employeeEmail,
    employeeId: formData.employeeId || formData.q5_employeeId,
    amount: parseFloat(formData.amount || formData.q6_amount),
    category: formData.category || formData.q7_category,
    merchant: formData.merchant || formData.q8_merchant,
    date: formData.date || formData.q9_date,
    description: formData.description || formData.q10_description,
    receiptUrl: formData.receiptUrl || formData.q11_receipt,
    submittedAt: new Date().toISOString()
  }
};

// Company policy rules and validation
const data = $input.first().json;

const policy = {
  maxAmounts: {
    meals: 75,
    travel: 500,
    office_supplies: 200,
    software: 1000,
    entertainment: 150,
    other: 100
  },
  approvedCategories: ['meals', 'travel', 'office_supplies', 'software', 'entertainment'],
  autoApproveThreshold: 100,
  managerApprovalThreshold: 500
};

const amount = data.amount;
const category = data.category;

let policyViolations = [];
let requiresApproval = 'auto';

if (!policy.approvedCategories.includes(category)) {
  policyViolations.push('Category not approved');
}

if (policy.maxAmounts[category] && amount > policy.maxAmounts[category]) {
  policyViolations.push('Amount exceeds category limit');
}

if (amount < policy.autoApproveThreshold && policyViolations.length === 0) {
  requiresApproval = 'auto';
} else if (amount >= policy.autoApproveThreshold && amount < policy.managerApprovalThreshold) {
  requiresApproval = 'manager';
} else if (amount >= policy.managerApprovalThreshold) {
  requiresApproval = 'director';
}

return {
  json: {
    ...data,
    policyViolations: policyViolations,
    isCompliant: policyViolations.length === 0,
    requiresApproval: requiresApproval,
    status: 'pending'
  }
};

