// PASTE YOUR KUBECONFIG CONTENT HERE
const kubeconfigContent = `
apiVersion: v1
kind: Config
clusters:
- cluster:
    certificate-authority-data: YOUR_CA_DATA
    server: https://your-k8s-api-server:6443
  name: your-cluster
contexts:
- context:
    cluster: your-cluster
    user: your-user
  name: your-context
current-context: your-context
users:
- name: your-user
  user:
    token: YOUR_TOKEN
`;

// Configuration
const namespace = 'production';

return [{
  json: {
    kubeconfig: kubeconfigContent,
    namespace: namespace
  }
}];

// Debug: Check inputs
const inputs = $input.all();

// Try to identify which input is which
let podsData, deploymentsData;

for (let i = 0; i < inputs.length; i++) {
  try {
    const input = inputs[i];

    if (!input.json?.stdout) continue;

    const data = JSON.parse(input.json.stdout);

    if (data.items && data.items.length > 0) {
      const firstKind = data.items[0].kind;

      if (firstKind === 'Pod') {
        podsData = data;
      } else if (firstKind === 'Deployment') {
        deploymentsData = data;
      }
    }
  } catch (e) {
    console.log(`Error parsing input ${i}:`, e.message);
  }
}

const pods = podsData?.items || [];
const deployments = deploymentsData?.items || [];
const namespace = podsData?.items?.[0]?.metadata?.namespace || deploymentsData?.items?.[0]?.metadata?.namespace || 'N/A';

const deploymentStatus = {};
const otherWorkloads = {};
const standalonePods = [];
const alerts = [];

// Process deployments from deployment objects
deployments.forEach(deployment => {
  const name = deployment.metadata.name;
  const ns = deployment.metadata.namespace;
  const replicas = deployment.spec.replicas || 0;
  const readyReplicas = deployment.status.readyReplicas || 0;
  const availableReplicas = deployment.status.availableReplicas || 0;

  deploymentStatus[name] = {
    name: name,
    namespace: ns,
    replicas: replicas,
    readyReplicas: readyReplicas,
    availableReplicas: availableReplicas,
    pods: []
  };

  if (readyReplicas < 1) {
    alerts.push({
      workload: name,
      kind: 'Deployment',
      issue: `No ready pods available! (Ready: ${readyReplicas}/${replicas})`
    });
  }
});

// Process pods - group by owner
pods.forEach(pod => {
  const podName = pod.metadata.name;
  const phase = pod.status.phase;
  const nodeName = pod.spec.nodeName || 'N/A';
  const conditions = pod.status.conditions || [];
  const readyCondition = conditions.find(c => c.type === 'Ready');
  const isReady = readyCondition?.status === 'True';
  const restartCount = pod.status.containerStatuses?.reduce((sum, c) => sum + c.restartCount, 0) || 0;

  const podInfo = {
    name: podName,
    phase: phase,
    ready: isReady,
    node: nodeName,
    restarts: restartCount
  };

  // Find owner from ownerReferences
  const ownerRefs = pod.metadata.ownerReferences || [];
  let ownerName = null;
  let ownerKind = null;

  for (const owner of ownerRefs) {
    if (owner.kind === 'ReplicaSet') {
      // Extract deployment name from ReplicaSet name
      const parts = owner.name.split('-');
      if (parts.length > 1) {
        parts.pop();
        ownerName = parts.join('-');
        ownerKind = 'Deployment';
      }
      break;
    } else if (['DaemonSet', 'StatefulSet', 'Node'].includes(owner.kind)) {
      ownerName = owner.name;
      ownerKind = owner.kind;
      break;
    }
  }

  // Add pod to appropriate owner
  if (ownerKind === 'Deployment') {
    // If deployment exists in deploymentStatus, add there
    if (deploymentStatus[ownerName]) {
      deploymentStatus[ownerName].pods.push(podInfo);
    } else {
      // Otherwise create a deployment entry (discovered from pods)
      if (!deploymentStatus[ownerName]) {
        const readyCount = 0; // Will be calculated later
        deploymentStatus[ownerName] = {
          name: ownerName,
          namespace: namespace,
          replicas: 0,
          readyReplicas: 0,
          availableReplicas: 0,
          pods: [],
          discoveredFromPods: true
        };
      }
      deploymentStatus[ownerName].pods.push(podInfo);
    }
  } else if (ownerName) {
    // Group by other owner types
    const key = `${ownerKind}/${ownerName}`;
    if (!otherWorkloads[key]) {
      otherWorkloads[key] = {
        kind: ownerKind,
        name: ownerName,
        pods: []
      };
    }
    otherWorkloads[key].pods.push(podInfo);
  } else {
    standalonePods.push(podInfo);
  }
});

// Calculate stats for deployments discovered from pods
Object.values(deploymentStatus).forEach(dep => {
  if (dep.discoveredFromPods) {
    const totalPods = dep.pods.length;
    const readyPods = dep.pods.filter(p => p.ready).length;
    dep.replicas = totalPods;
    dep.readyReplicas = readyPods;
    dep.availableReplicas = readyPods;

    if (readyPods < 1) {
      alerts.push({
        workload: dep.name,
        kind: 'Deployment',
        issue: `No ready pods available! (Ready: ${readyPods}/${totalPods})`
      });
    }
  }
});

// Check for alerts in non-deployment workloads
Object.values(otherWorkloads).forEach(owner => {
  const readyCount = owner.pods.filter(p => p.ready).length;
  if (readyCount < 1) {
    alerts.push({
      workload: owner.name,
      kind: owner.kind,
      issue: `No ready pods available! (Ready: ${readyCount}/${owner.pods.length})`
    });
  }
});

// Generate report
let markdown = `# Kubernetes Cluster Status Report\n\n`;
markdown += `**Namespace:** ${namespace}\n`;
markdown += `**Timestamp:** ${new Date().toISOString()}\n\n`;

const deploymentCount = Object.keys(deploymentStatus).length;
const otherWorkloadCount = Object.keys(otherWorkloads).length;
markdown += `**Total:** ${deploymentCount} deployments, ${otherWorkloadCount} other workloads, ${pods.length} pods\n\n`;

// Deployments section
if (deploymentCount > 0) {
  markdown += `## Deployments\n\n`;
  Object.values(deploymentStatus).forEach(dep => {
    const status = dep.readyReplicas >= 1 ? '‚úÖ' : '‚ùå';
    markdown += `### ${status} ${dep.name}\n`;
    markdown += `- **Replicas:** ${dep.readyReplicas}/${dep.replicas}\n`;
    markdown += `- **Available:** ${dep.availableReplicas}\n`;
    markdown += `- **Pods:**\n`;

    if (dep.pods.length === 0) {
      markdown += `  - *No pods*\n`;
    } else {
      dep.pods.forEach(pod => {
        const podStatus = pod.ready ? '‚úÖ' : '‚ùå';
        markdown += `  - ${podStatus} ${pod.name} (${pod.phase}) - Node: ${pod.node}, Restarts: ${pod.restarts}\n`;
      });
    }
    markdown += `\n`;
  });
}

// Other workloads section (DaemonSets, StatefulSets, Static Pods)
if (otherWorkloadCount > 0) {
  markdown += `## Other Workloads (DaemonSets, StatefulSets, Static Pods)\n\n`;
  Object.values(otherWorkloads).forEach(owner => {
    const readyCount = owner.pods.filter(p => p.ready).length;
    const totalCount = owner.pods.length;
    const status = readyCount >= 1 ? '‚úÖ' : '‚ùå';

    markdown += `### ${status} ${owner.name} (${owner.kind})\n`;
    markdown += `- **Ready:** ${readyCount}/${totalCount}\n`;
    markdown += `- **Pods:**\n`;

    owner.pods.forEach(pod => {
      const podStatus = pod.ready ? '‚úÖ' : '‚ùå';
      markdown += `  - ${podStatus} ${pod.name} (${pod.phase}) - Node: ${pod.node}, Restarts: ${pod.restarts}\n`;
    });
    markdown += `\n`;
  });
}

// Standalone pods
if (standalonePods.length > 0) {
  markdown += `## Standalone Pods\n\n`;
  standalonePods.forEach(pod => {
    const podStatus = pod.ready ? '‚úÖ' : '‚ùå';
    markdown += `- ${podStatus} ${pod.name} (${pod.phase}) - Node: ${pod.node}, Restarts: ${pod.restarts}\n`;
  });
  markdown += `\n`;
}

// Alerts section
if (alerts.length > 0) {
  markdown += `## ‚ö†Ô∏è Alerts\n\n`;
  alerts.forEach(alert => {
    markdown += `- **${alert.workload}** (${alert.kind}): ${alert.issue}\n`;
  });
}

// Convert markdown to binary data
const buffer = Buffer.from(markdown, 'utf-8');
const binaryData = {
  data: buffer.toString('base64'),
  mimeType: 'text/markdown',
  fileName: 'report.md'
};

return [{
  json: {
    markdown: markdown,
    hasAlerts: alerts.length > 0,
    alerts: alerts,
    deploymentCount: deploymentCount,
    podCount: pods.length,
    namespace: namespace
  },
  binary: {
    data: binaryData
  }
}];
