// Classify YouTube videos by like count
const THRESHOLD = 1000;

function extractNumberFrom(...candidates) {
  for (const v of candidates) {
    if (v === undefined || v === null) continue;
    if (typeof v === 'number' && Number.isFinite(v)) return v;
    if (typeof v === 'string') {
      const digits = v.replace(/\D/g, '');
      if (digits.length > 0) return parseInt(digits, 10);
    }
  }
  return null;
}

function processVideo(video) {
  const v = JSON.parse(JSON.stringify(video));
  const likeCount = extractNumberFrom(v?.statistics?.likeCount);
  const videoId = v?.id || null;

  let classification = 'unknown';
  let needsStatsFetch = false;

  if (likeCount === null) {
    needsStatsFetch = true;
  } else if (likeCount >= THRESHOLD) {
    classification = 'viral';
  } else {
    classification = 'normal';
  }

  return {
    json: {
      videoId,
      title: v?.snippet?.title ?? null,
      likeCount,
      classification,
      needsStatsFetch,
      original: v
    }
  };
}

const allResults = [];
for (const item of items) {
  const payload = item.json || {};
  if (Array.isArray(payload.items)) {
    for (const video of payload.items) allResults.push(processVideo(video));
  } else if (payload.kind && payload.kind.includes('youtube#video')) {
    allResults.push(processVideo(payload));
  }
}
return allResults;

// Collect all post texts from previous steps and aggregate into one string
const allPosts = [];
for (const item of items) {
  const content = item.json?.choices?.[0]?.message?.content;
  if (content) allPosts.push(content);
}
const combinedText = allPosts.join('\n\n---\n\n');
return [{ json: { weekly_summary_input: combinedText } }];
