// Function: Text in mehrere Zeilen aufteilen wenn > 50.000 Zeichen
const MAX_LENGTH = 50000;
const items = [];

for (const item of $input.all()) {
  const text = item.json.cleanText || item.json.Text || '';
  const absender = item.json.Absenderadresse || '';
  const absenderName = item.json.Absendername || '';
  const betreff = item.json.Betreff || '';

  // Wenn Text kurz genug ist: normal durchreichen
  if (text.length <= MAX_LENGTH) {
    items.push({
      json: {
        Absenderadresse: absender,
        Absendername: absenderName,
        Betreff: betreff,
        Text: text,
        Teil: '1/1',
        Zeichenanzahl: text.length
      }
    });
    continue;
  }

  // Text ist zu lang: in Chunks aufteilen
  const chunks = [];
  let remaining = text;

  while (remaining.length > 0) {
    let chunk = remaining.substring(0, MAX_LENGTH);

    // Versuche bei Absatz-Ende zu schneiden (nicht mitten im Wort)
    if (remaining.length > MAX_LENGTH) {
      const lastNewline = chunk.lastIndexOf('

');
      const lastPeriod = chunk.lastIndexOf('. ');

      // Schneide bei Absatz oder Satzende
      const cutPoint = lastNewline > MAX_LENGTH - 500 ? lastNewline : 
                       lastPeriod > MAX_LENGTH - 200 ? lastPeriod + 1 : 
                       MAX_LENGTH;

      chunk = remaining.substring(0, cutPoint);
    }

    chunks.push(chunk.trim());
    remaining = remaining.substring(chunk.length).trim();
  }

  // F√ºr jeden Chunk ein Item erstellen
  chunks.forEach((chunk, index) => {
    items.push({
      json: {
        Absenderadresse: absender,
        Absendername: absenderName,
        Betreff: `${betreff} (Teil ${index + 1}/${chunks.length})`,
        Text: chunk,
        Teil: `${index + 1}/${chunks.length}`,
        Zeichenanzahl: chunk.length
      }
    });
  });
}

return items;

