// n8n stores binary files in items[0].binary
const binaryData = items[0].binary.data; // do NOT use .data.data
const base64Image = binaryData.data.toString('base64');

items[0].json.base64 = base64Image;
return items;

// Get OCR text from Google Vision HTTP Request output
const rawText = $json.responses?.[0]?.fullTextAnnotation?.text || "";

// Pass it along to OpenAI
return [{ json: { raw_text: rawText } }];

// Step 1: Get AI Output (stringify if needed)
let aiOutput = $json.output;
if (typeof aiOutput !== 'string') {
  aiOutput = JSON.stringify(aiOutput);
}

// Step 2: Clean Markdown or code fences
aiOutput = aiOutput
  .replace(/^```json\s*/i, '')
  .replace(/^```/, '')
  .replace(/```$/, '')
  .trim();

// Step 3: Try parsing JSON
let parsed;
try {
  parsed = JSON.parse(aiOutput);
} catch (error) {
  throw new Error('‚ùå Failed to parse AI output. Check format.\n\nOutput received:\n' + aiOutput);
}

// Step 4: Handle array or single object
const data = Array.isArray(parsed) ? parsed[0] : parsed;

// Step 5: Extract subject & htmlBody (fallback if only email_draft present)
let subject = data.subject || '';
let htmlBody = data.htmlBody || '';

if (!subject && data.email_draft) {
  const match = data.email_draft.match(/^Subject:\s*(.+?)\n\n([\s\S]*)/i);
  if (match) {
    subject = match[1].trim();
    htmlBody = match[2].replace(/\n/g, '<br>').trim();
  } else {
    htmlBody = data.email_draft.replace(/\n/g, '<br>').trim();
  }
}

// Step 6: Normalize arrays and optional fields
const emails = (Array.isArray(data.emails) ? data.emails : data.email ? [data.email] : []).map(e => e.trim());
const phones = (Array.isArray(data.phones) ? data.phones : data.phone ? [data.phone] : []).map(p => {
  if (!p) return '';
  let cleaned = p.replace(/[\s\-]+/g, '');
  return "'" + cleaned;
});

// Step 7: Build clean, consistent output
return [{
  json: {
    name: data.name || '',
    company: data.company || '',
    role: data.role || '',
    emails,
    phones,
    country: data.country || '',
    website: data.website || '',
    one_line_summary: data.one_line_summary || '',
    opportunities: data.opportunities || [],
    opportunities_summary: data.opportunities_summary || '',
    fit_score: data.fit_score || 0,
    fit_reason: data.fit_reason || '',
    subject,
    htmlBody,
    whatsapp_draft: data.whatsapp_draft || '',
    next_actions: data.next_actions || []
  }
}];
