// Reproducible dummy metrics for last 7 days, by channel & campaign.
// No external API needed. Good for demos.

// --- helpers ---
function seededRandom(seed) {
  // simple LCG
  let s = seed % 2147483647;
  if (s <= 0) s += 2147483646;
  return () => (s = s * 16807 % 2147483647) / 2147483647;
}
const seed = 20250601; // fix for reproducible demo
const rand = seededRandom(seed);

function round(n, d=0) {
  const p = Math.pow(10,d);
  return Math.round(n*p)/p;
}

function dateISO(d) {
  return d.toISOString().slice(0,10);
}

// --- period (last 7 days) ---
const end = new Date(); // today
const start = new Date(end);
start.setDate(end.getDate() - 6); // inclusive 7 days

// --- channels & base volumes ---
const channels = [
  { key: 'google_ads',  name: 'Google Ads',     baseImp: 140000, baseCpc: 0.6,  baseConvRate: 0.018 },
  { key: 'meta_ads',    name: 'Meta Ads',       baseImp: 120000, baseCpc: 0.45, baseConvRate: 0.015 },
  { key: 'tiktok_ads',  name: 'TikTok Ads',     baseImp:  90000, baseCpc: 0.35, baseConvRate: 0.012 },
  { key: 'youtube_ads', name: 'YouTube Ads',    baseImp: 110000, baseCpc: 0.40, baseConvRate: 0.010 },
];

const campaignsByChannel = {
  google_ads:  ['Brand Search', 'Competitor Search', 'Non-Brand Generic'],
  meta_ads:    ['Prospecting Video', 'Retargeting Carousel', 'Broad Static'],
  tiktok_ads:  ['Spark Ads UGC', 'Creator Whitelist', 'TopView Test'],
  youtube_ads: ['In-Stream Skippable', 'In-Feed Shorts', 'Remarketing']
};

// --- generate data ---
let totals = { impressions: 0, clicks: 0, conversions: 0, spend: 0, revenue: 0 };
const byChannel = [];

for (const ch of channels) {
  const chImpressions = Math.floor(ch.baseImp * (0.9 + rand()*0.3)); // ¬±15%
  const chClicks = Math.floor(chImpressions * (0.01 + rand()*0.02)); // 1%‚Äì3%
  const chCpc = round(ch.baseCpc * (0.85 + rand()*0.3), 2);
  const chSpend = round(chClicks * chCpc, 2);
  const chConvRate = ch.baseConvRate * (0.8 + rand()*0.4); // ¬±20%
  const chConversions = Math.floor(chClicks * chConvRate);
  const aov = 38 + rand()*22; // average order value 38‚Äì60
  const chRevenue = round(chConversions * aov, 2);
  const chRoas = chSpend > 0 ? round(chRevenue / chSpend, 2) : null;

  // campaigns
  const campNames = campaignsByChannel[ch.key];
  const campaigns = [];
  let remImp = chImpressions, remClicks = chClicks, remConv = chConversions, remSpend = chSpend, remRev = chRevenue;

  for (let i=0;i<campNames.length;i++) {
    const share = i < campNames.length-1 ? (0.3 + rand()*0.5) : 1; // last gets remainder
    const imp = i < campNames.length-1 ? Math.max(0, Math.floor(remImp * share * 0.4)) : remImp;
    const clk = i < campNames.length-1 ? Math.max(0, Math.floor(remClicks * share * 0.4)) : remClicks;
    const conv= i < campNames.length-1 ? Math.max(0, Math.floor(remConv * share * 0.4)) : remConv;
    const sp  = i < campNames.length-1 ? round(remSpend * share * 0.4, 2) : round(remSpend,2);
    const rev = i < campNames.length-1 ? round(remRev * share * 0.4, 2) : round(remRev,2);
    campaigns.push({
      name: campNames[i],
      impressions: imp,
      clicks: clk,
      conversions: conv,
      spend: sp,
      revenue: rev,
      roas: sp>0 ? round(rev/sp,2) : null,
      cpc: clk>0 ? round(sp/clk,2) : null,
      ctr: imp>0 ? round((clk/imp)*100,2) : null,
      convRate: clk>0 ? round((conv/clk)*100,2) : null,
    });
    remImp -= imp; remClicks -= clk; remConv -= conv; remSpend = round(remSpend - sp,2); remRev = round(remRev - rev,2);
  }

  totals.impressions += chImpressions;
  totals.clicks += chClicks;
  totals.conversions += chConversions;
  totals.spend = round(totals.spend + chSpend, 2);
  totals.revenue = round(totals.revenue + chRevenue, 2);

  byChannel.push({
    key: ch.key,
    name: ch.name,
    impressions: chImpressions,
    clicks: chClicks,
    conversions: chConversions,
    spend: chSpend,
    revenue: chRevenue,
    roas: chRoas,
    cpc: chCpc,
    ctr: chImpressions>0 ? round((chClicks/chImpressions)*100,2) : null,
    convRate: chClicks>0 ? round((chConversions/chClicks)*100,2) : null,
    campaigns
  });
}

const period = { start: dateISO(start), end: dateISO(end) };
const summary = {
  impressions: totals.impressions,
  clicks: totals.clicks,
  conversions: totals.conversions,
  spend: totals.spend,
  revenue: totals.revenue,
  roas: totals.spend>0 ? round(totals.revenue/totals.spend,2) : null,
  ctr: totals.impressions>0 ? round((totals.clicks/totals.impressions)*100,2) : null,
  convRate: totals.clicks>0 ? round((totals.conversions/totals.clicks)*100,2) : null,
};

return {
  period,
  summary,
  byChannel
};

