/**
 * Build a weekly report in Markdown from fake metrics + LLM executive summary.
 * IMPORTANT: Set these to your exact node names in the left sidebar.
 */
const METRICS_NODE = "Google Ads Demo";   // <‚Äî dein Metrics-Code-Node
const SUMMARY_NODE = "Message a model";   // <‚Äî dein LLM-Node

// ---- pull data from the referenced nodes ----
const metrics = $node[METRICS_NODE]?.json;
if (!metrics) {
  throw new Error(`Metrics node "${METRICS_NODE}" not found or has no JSON output.`);
}
const period = metrics.period || {};
const S = metrics.summary || {};
const channels = metrics.byChannel || [];

const exec = $node[SUMMARY_NODE]?.json?.message?.content
          || "No executive summary available.";

// ---- helpers ----
function fmt(n, d=0) {
  if (n === null || n === undefined) return "-";
  return Number(n).toLocaleString(undefined, { maximumFractionDigits: d });
}

function mdChannelTable(rows) {
  const header = `| Channel | Impr. | Clicks | Conv. | Spend | Revenue | ROAS | CTR | CVR |
|---|---:|---:|---:|---:|---:|---:|---:|---:|`;
  const lines = rows.map(r =>
    `| ${r.name} | ${fmt(r.impressions)} | ${fmt(r.clicks)} | ${fmt(r.conversions)} | $${fmt(r.spend,2)} | $${fmt(r.revenue,2)} | ${fmt(r.roas,2)} | ${fmt(r.ctr,2)}% | ${fmt(r.convRate,2)}% |`
  );
  return [header, ...lines].join('\n');
}

function mdTopCampaigns(rows) {
  const all = [];
  for (const ch of rows) {
    for (const c of (ch.campaigns || [])) all.push({ channel: ch.name, ...c });
  }
  all.sort((a,b) => (b.roas ?? 0) - (a.roas ?? 0));
  const top = all.slice(0,3);
  const header = `| Campaign | Channel | ROAS | Spend | Revenue | CTR | CVR |
|---|---|---:|---:|---:|---:|---:|`;
  const lines = top.map(t =>
    `| ${t.name} | ${t.channel} | ${fmt(t.roas,2)} | $${fmt(t.spend,2)} | $${fmt(t.revenue,2)} | ${fmt(t.ctr,2)}% | ${fmt(t.convRate,2)}% |`
  );
  return [header, ...lines].join('\n');
}

// ---- assemble markdown ----
const md = [
  `# Weekly Performance Report`,
  `**Period:** ${period.start ?? "-"} ‚Üí ${period.end ?? "-"}`,
  ``,
  `## Executive Summary`,
  exec,
  ``,
  `## Totals`,
  `- Impressions: ${fmt(S.impressions)}`,
  `- Clicks: ${fmt(S.clicks)}`,
  `- Conversions: ${fmt(S.conversions)}`,
  `- Spend: $${fmt(S.spend,2)}`,
  `- Revenue: $${fmt(S.revenue,2)}`,
  `- ROAS: ${fmt(S.roas,2)}`,
  `- CTR: ${fmt(S.ctr,2)}%`,
  `- Conversion Rate: ${fmt(S.convRate,2)}%`,
  ``,
  `## Performance by Channel`,
  channels.length ? mdChannelTable(channels) : "_No channel data._",
  ``,
  `## Top Campaigns (by ROAS)`,
  channels.length ? mdTopCampaigns(channels) : "_No campaigns available._",
  ``,
  `*Generated automatically by n8n.*`
].join('\n');

return {
  report: md,
  period,
  summary: S,
  byChannel: channels
};

