const user = $input.item.json;
const inputPassword = $('Verify Input').item.json.password;
const [salt, storedHash] = user.password_hash.split(':');

if (!salt || !storedHash) {
  throw new Error('Invalid password format in database');
}

return {
  json: {
    userId: user.id,
    email: user.email,
    username: user.username,
    salt: salt,
    storedHash: storedHash,
    passwordWithSalt: inputPassword + salt
  }
};

const header = $('Create JWT Payload').first().json.header;
const accessPayload = $input.item.json.accessPayload;
const refreshPayload = $input.item.json.refreshPayload;
const accessSignature = $input.item.json.accessSignature;
const refreshSignature = $input.item.json.refreshSignature;

const accessSigBase64url = Buffer.from(accessSignature, 'base64')
  .toString('base64url');
const refreshSigBase64url = Buffer.from(refreshSignature, 'base64')
  .toString('base64url');

const accessJWT = `${header}.${accessPayload}.${accessSigBase64url}`;
const refreshJWT = `${header}.${refreshPayload}.${refreshSigBase64url}`;

return {
  json: {
    userId: $input.item.json.userId,
    email: $input.item.json.email,
    username: $input.item.json.username,
    accessToken: accessJWT,
    refreshToken: refreshJWT
  }
};

const token = $input.first().json.access_token;

if (!token) {
  throw new Error('Token is required');
}

const parts = token.split('.');

if (parts.length !== 3) {
  throw new Error('Invalid token format');
}

const [header, payload, signature] = parts;

const decodedPayload = JSON.parse(
  Buffer.from(payload, 'base64url').toString()
);

const now = Math.floor(Date.now() / 1000);
if (decodedPayload.exp < now) {
  throw new Error('Token expired');
}

if (decodedPayload.type === 'refresh') {
  throw new Error('Invalid token type');
}

return {
  json: {
    header,
    payload,
    access_token: token,
    providedSignature: signature,
    signatureInput: `${header}.${payload}`,
    userId: decodedPayload.userId,
    email: decodedPayload.email
  }
};

const providedSignature = $('Parse JWT').first().json.providedSignature;
const expectedSignature = $input.item.json.expectedSignature;

const providedBase64 = Buffer.from(providedSignature, 'base64url')
  .toString('base64');

if (providedBase64 !== expectedSignature) {
  throw new Error('Invalid token signature');
}

return {
  json: {
    valid: true,
    userId: $input.item.json.userId,
    email: $input.item.json.email
  }
};

