const userId = $input.item.json.userId;
const email = $input.item.json.email;
const username = $input.item.json.username;

const now = Math.floor(Date.now() / 1000);

const accessPayload = {
  userId: userId,
  email: email,
  iat: now,
  exp: now + (15 * 60)
};

const refreshPayload = {
  userId: userId,
  type: 'refresh',
  iat: now,
  exp: now + (7 * 24 * 60 * 60)
};

const header = Buffer.from(JSON.stringify({alg: 'HS256', typ: 'JWT'}))
  .toString('base64url');

const accessPayloadEncoded = Buffer.from(JSON.stringify(accessPayload))
  .toString('base64url');
const refreshPayloadEncoded = Buffer.from(JSON.stringify(refreshPayload))
  .toString('base64url');

return {
  json: {
    userId,
    email,
    username,
    header,
    accessPayload: accessPayloadEncoded,
    refreshPayload: refreshPayloadEncoded,
    accessSignatureInput: `${header}.${accessPayloadEncoded}`,
    refreshSignatureInput: `${header}.${refreshPayloadEncoded}`
  }
};

