let new_items = []
let index = 0
for (const item of $input.all()) {
  let userMessage = "<transcribed_audio>" + item.json.content.parts[0].text + "</transcribed_audio>";

  new_items.push({
    "userMessage": userMessage
  })

  index += 1;
}

return new_items;

let new_items = []
let index = 0
for (const item of $input.all()) {
  let userMessage = "<extracted_image_content>" + item.json.candidates[0].content.parts[0].text + "</extracted_image_content>";
  let text = $('Switch').itemMatching(index).json.message.caption
  if (text) {
    userMessage += "\n" + text;
  }

  new_items.push({
    "userMessage": userMessage
  })

  index += 1;
}

return new_items;

