const items = $input.all();

// Map to extract outputs and identify sections
const sections = {
  implementation: '',
  linkedin: '',
  discord: '',
  usecase: '',
  creator: ''
};

// Extract content - now it's directly in item.json.output
items.forEach((item, index) => {
  const content = item.json.output || '';

  // Assign based on order from your workflow
  switch(index) {
    case 0: sections.linkedin = content; break;
    case 1: sections.discord = content; break;
    case 2: sections.implementation = content; break;
    case 3: sections.usecase = content; break;
    case 4: sections.creator = content; break;
