// Inputs expected from Aggregate/RSS: arrays: title[], content[], link[]
const all = items[0].json;              // aggregated item
const titles = all.title || [];
const contents = all.content || [];
const links = all.link || [];

const articles = [];
const lines = [];

const maxLen = Math.max(titles.length, contents.length, links.length);
for (let i = 0; i < maxLen; i++) {
  const obj = {
    id: i + 1,                          // stable ID (1..N). Use guid if you have one.
    title: titles[i] ?? "",
    content: contents[i] ?? "",
    link: links[i] ?? ""
  };
  articles.push(obj);
  // Short preview for the AI to choose from:
  const preview = (obj.content || "").replace(/\s+/g, " ").slice(0, 220);
  lines.push(`${obj.id}. ${obj.title}\n${preview}${preview.length === 220 ? "‚Ä¶" : ""}`);
}

return [
  {
    json: {
      articles,                         // keep the structured mapping
      forAI: lines.join("\n\n")         // human-readable list for the model
    }
  }
];
