const items = $input.all();

// Group responses by rating
const groupedByRating = {
  positive: [],
  neutral: [],
  negative: []
};

for (const item of items) {
  // Google Sheet„ÅÆÊó•Êú¨Ë™ûÂàóÂêç„ÇíÊ≠£„Åó„ÅèÂèÇÁÖß„Åô„Çã„Çà„ÅÜ„Å´‰øÆÊ≠£
  const rating = parseInt(item.json["Ê∫ÄË∂≥Â∫¶ (Rating)"] || 0);
  const feedback = item.json["Ëá™Áî±Ë®òËø∞„Ç≥„É°„É≥„Éà (Comment)"] || '';
  const date = item.json["ÂõûÁ≠îÊó•ÊôÇ (Timestamp)"] || new Date().toISOString();

  const response = {
    rating: rating,
    feedback: feedback,
    date: date
  };

  // --- „Åì„Åì„Åã„Çâ‰øÆÊ≠£ ---
  // 5ÊÆµÈöéË©ï‰æ°„Å´Âêà„Çè„Åõ„Å¶ÊÑüÊÉÖ„ÇíÂàÜÈ°û
  if (rating >= 4) {
    // 4ÁÇπ‰ª•‰∏ä„Å™„Çâ "positive"
    groupedByRating.positive.push(response);
  } else if (rating === 3) {
    // 3ÁÇπ„Å™„Çâ "neutral"
    groupedByRating.neutral.push(response);
  } else {
    // 3ÁÇπÊú™Ê∫Ä„Å™„Çâ "negative"
    groupedByRating.negative.push(response);
  }
  // --- „Åì„Åì„Åæ„Åß‰øÆÊ≠£ ---
}

// Prepare batches for AI processing
const batches = [];

for (const [sentiment, responses] of Object.entries(groupedByRating)) {
  if (responses.length > 0) {
    batches.push({
      sentiment: sentiment,
      count: responses.length,
      responses: responses.slice(0, 50) // 1„Éê„ÉÉ„ÉÅ„ÅÇ„Åü„Çä„ÅÆÂõûÁ≠îÊï∞„Çí50„Å´Âà∂Èôê
