// Aggregate all AI analysis results
const allAnalyses = $input.all();
const timestamp = new Date().toISOString();
const totalResponses = allAnalyses.reduce((sum, item) => sum + (item.json.originalCount || 0), 0);

// Combine all themes
const allThemes = [];
const themeMap = new Map();

for (const analysis of allAnalyses) {
  // --- „Åì„Åì„Åã„Çâ‰øÆÊ≠£ ---
  // "output" „Ç≠„Éº„ÅÆ‰∏≠„Å´„ÅÇ„Çã themes „ÇíÂèÇÁÖß„Åô„Çã„Çà„ÅÜ„Å´Â§âÊõ¥
  if (analysis.json.output && analysis.json.output.themes) {
    for (const theme of analysis.json.output.themes) {
  // --- „Åì„Åì„Åæ„Åß‰øÆÊ≠£ ---
      const key = theme.theme.toLowerCase();
      if (themeMap.has(key)) {
        const existing = themeMap.get(key);
        existing.examples = [...existing.examples, ...theme.examples];
        existing.frequency = 'Multiple mentions';
      } else {
        themeMap.set(key, { ...theme });
      }
    }
  }
}

// Convert map to array and sort by frequency
const consolidatedThemes = Array.from(themeMap.values())
  .sort((a, b) => b.examples.length - a.examples.length)
  .slice(0, 10);

// Combine all insights and recommendations
const allInsights = [];
const allRecommendations = [];
const sentimentBreakdown = {};

for (const analysis of allAnalyses) {
  const sentiment = analysis.json.originalSentiment || 'unknown';
  sentimentBreakdown[sentiment] = analysis.json.originalCount || 0;

  // --- „Åì„Åì„Åã„Çâ‰øÆÊ≠£ ---
  // "output" „Ç≠„Éº„ÅÆ‰∏≠„Å´„ÅÇ„Çã insights „Å® recommendations „ÇíÂèÇÁÖß
  if (analysis.json.output && analysis.json.output.insights) {
    allInsights.push(...analysis.json.output.insights);
  }
  if (analysis.json.output && analysis.json.output.recommendations) {
    allRecommendations.push(...analysis.json.output.recommendations);
  }
  // --- „Åì„Åì„Åæ„Åß‰øÆÊ≠£ ---
}

// Create final summary
const summary = {
  reportDate: timestamp,
  totalResponsesAnalyzed: totalResponses,
  sentimentBreakdown: sentimentBreakdown,
  topThemes: consolidatedThemes,
  keyInsights: [...new Set(allInsights)].slice(0, 10),
  priorityRecommendations: [...new Set(allRecommendations)].slice(0, 7),
  executiveSummary: `Analysis of ${totalResponses} customer survey responses revealed ${consolidatedThemes.length} key themes. Overall sentiment distribution: Positive (${sentimentBreakdown.positive || 0}), Neutral (${sentimentBreakdown.neutral || 0}), Negative (${sentimentBreakdown.negative || 0}).`
};

return [summary];

// Add metadata to each analysis result
const item = $input.first();
const loopData = $('Loop Over Batches').first();

return {
  ...item.json,
  originalSentiment: loopData.json.sentiment,
  originalCount: loopData.json.count
};
