const now = new Date();
const lastMonth = new Date(now.getFullYear(), now.getMonth() - 1, 1);
const thisMonth = new Date(now.getFullYear(), now.getMonth(), 1);
const eligibleNominations = [];

for (const item of $input.all()) {
  const nomination = item.json;
  if (nomination.eligibleForMonthlyAward !== 'Yes') continue;
  const nominationDate = new Date(nomination.timestamp);
  if (nominationDate >= lastMonth && nominationDate < thisMonth) {
    eligibleNominations.push({
      json: {
        ...nomination,
        monthYear: lastMonth.toLocaleString('default', { month: 'long', year: 'numeric' })
      }
    });
  }
}

eligibleNominations.sort((a, b) => {
  const scoreA = (parseFloat(a.json.recognitionStrength) + parseFloat(a.json.impactScore)) / 2;
  const scoreB = (parseFloat(b.json.recognitionStrength) + parseFloat(b.json.impactScore)) / 2;
  return scoreB - scoreA;
});

return eligibleNominations;
