// Extract base64 image from Gemini response
const imageData = $input.item.json.candidates[0].content.parts[0].inlineData.data;
const mimeType = $input.item.json.candidates[0].content.parts[0].inlineData.mimeType;

// Convert base64 to binary for temporary storage
const buffer = Buffer.from(imageData, 'base64');

// Create a data URL for VideoBGRemover API
const dataUrl = `data:${mimeType};base64,${imageData}`;

return {
  json: {
    background_image_data: imageData,
    background_image_mime: mimeType,
    background_image_url: dataUrl
  },
  binary: {
    data: {
      data: buffer,
      mimeType: mimeType,
      fileName: 'generated_background.png'
    }
  }
};

