parameters.jsCode:
const items = [];

for (const output of $input.all()) {
  const workoutArray = [{
    "category": "WORKOUT",
    "type": output.json.output.workoutType,
    "start_date_local": output.json.output.workoutDate,
    "name": output.json.output.workoutName,
    "description": output.json.output.workoutDescription,
    "external_id": output.json.output.externalId
  }];

  items.push({
    json: { workoutArray: workoutArray }
  });
}

return items;
