// Extract and prepare message data
const item = $input.item.json;
const messageData = {
  text: item.message?.text || item.message?.caption || '',
  from: item.message?.from?.username || 'unknown',
  chatId: item.message?.chat?.id,
  messageId: item.message?.message_id,
  timestamp: new Date().toISOString(),
  hasPhoto: !!item.message?.photo,
  hasDocument: !!item.message?.document,
  hasAudio: !!item.message?.audio,
  hasVoice: !!item.message?.voice,
  photoFileId: item.message?.photo?.[item.message.photo.length - 1]?.file_id || null,
  audioFileId: item.message?.audio?.file_id || null,
  voiceFileId: item.message?.voice?.file_id || null
};

// Determine content type
if (messageData.hasPhoto) {
  messageData.contentType = 'image';
} else if (messageData.hasAudio) {
  messageData.contentType = 'audio';
} else if (messageData.hasVoice) {
  messageData.contentType = 'voice';
} else if (messageData.hasDocument) {
  messageData.contentType = 'document';
} else {
  messageData.contentType = 'text';
}

console.log('üì• Processing message:', messageData);
return { json: messageData };

// Process NVIDIA API response and convert base64 to binary
const items = $input.all();

return items.map(item => {
  // NVIDIA API returns base64 image in 'image' field
  let base64Image;

  // Check different possible response formats
  if (item.json.image) {
    base64Image = item.json.image;
  } else if (item.json.artifacts && item.json.artifacts[0]) {
    base64Image = item.json.artifacts[0].base64;
  } else if (item.json.data && item.json.data[0]) {
    base64Image = item.json.data[0].b64_json;
  }

  // Clean base64 string (remove data URL prefix if present)
  const base64Clean = base64Image.replace(/^data:image\/\w+;base64,/, '');

  // Convert to buffer
  const buffer = Buffer.from(base64Clean, 'base64');

  return {
    json: {
      fileName: 'generated-image.png',
      mimeType: 'image/png',
      prompt: item.json.prompt || 'Generated image'
    },
    binary: {
      data: {
        data: buffer.toString('base64'),
        mimeType: 'image/png',
        fileName: 'generated-image.png'
      }
    }
  };
});
