// n8n Code Node - Product Variant Processor
// This processes products and groups them by variants
const items = $input.all();

// Helper function to extract base SKU and variant suffix
function parseVariantSKU(sku) {
  const skuStr = String(sku);
  const parts = skuStr.split('-');

  if (parts.length > 1) {
    const baseSKU = parts[0];
    const variantSuffix = parts.slice(1).join('-');
    return { baseSKU, variantSuffix, isVariant: true };
  }

  return { baseSKU: skuStr, variantSuffix: '', isVariant: false };
}

// Group products by Product Name
const productGroups = {};

items.forEach(item => {
  const data = item.json;
  const productName = data['Product Name'];

  if (!productGroups[productName]) {
    productGroups[productName] = [];
  }

  productGroups[productName].push(data);
});

// Build output array
const output = [];

Object.keys(productGroups).forEach(productName => {
  const variants = productGroups[productName];
  const hasVariants = variants.length > 1 || (variants.length === 1 && variants[0].Size !== '');

  if (hasVariants) {
    // Product with variants - prepare all variant data
    const totalInventory = variants.reduce((sum, v) => sum + v['On hand Inventory'], 0);
    const basePrice = variants[0].Price;
    const baseSKU = parseVariantSKU(variants[0].SKU).baseSKU;

    // Build optionsGraph dynamically based on variant count
    const optionsGraph = [
      {
        name: "Size",
        values: variants.map(v => {
          const parsed = parseVariantSKU(v.SKU);
          return { name: v.Size || parsed.variantSuffix };
        })
      }
    ];

    output.push({
      json: {
        type: 'variant',
        productName: productName,
        baseSKU: baseSKU,
        basePrice: basePrice,
        totalInventory: totalInventory,
        variantCount: variants.length,
        productImage: variants[0]['Product Image'],
        // GraphQL options format
        optionsGraph: optionsGraph,
        // Format variants for Shopify productCreate mutation
        variantsForCreate: variants.map(v => {
          const parsed = parseVariantSKU(v.SKU);
          return {
            optionValues: [{ optionName: "Size", name: v.Size || parsed.variantSuffix }],
            price: String(v.Price),
            inventoryItem: {
              sku: String(v.SKU),
              tracked: true
            },
            inventoryQuantities: {
              availableQuantity: v['On hand Inventory'],
              locationId: "LOCATION_ID_PLACEHOLDER" // Will be replaced
            }
          };
        }),
        // Keep individual variant data for inventory updates
        variants: variants.map(v => {
          const parsed = parseVariantSKU(v.SKU);
          return {
            productName: productName,
            fullSKU: String(v.SKU),
            variantTitle: v.Size || parsed.variantSuffix,
            size: v.Size,
            inventory: v['On hand Inventory'],
            image: variants[0]['Product Image'],
            price: v.Price
          };
        })
      }
    });
  } else {
    // Single product (no variants)
    const product = variants[0];
    const parsed = parseVariantSKU(product.SKU);

    output.push({
      json: {
        type: 'single',
        productName: productName,
        baseSKU: parsed.baseSKU,
        fullSKU: String(product.SKU),
        price: product.Price,
        inventory: product['On hand Inventory'],
        productImage: product['Product Image']
      }
    });
  }
});

return output;

// Get ALL items from the previous step (all created products)
const items = $input.all();

// Get the original variants data from Switch node (all variant products)
const switchItems = $('is variant?').all();
// Process each product
const output = items.map((item, index) => {
  // Get the created product data
  const productData = item.json.data.productCreate.product;
  const productId = productData.id;
  const mainVariant = item.json.data.productCreate.product.variants.edges[0].node.id
  const mediaId = item.json.data.productCreate.product.media.edges[0].node.id

  // Get the Size option - it should exist now
  const sizeOption = productData.options.find(opt => opt.name === "Size");

  if (!sizeOption) {
    throw new Error(`Size option not found in created product: ${productData.title}`);
  }

  // Get the corresponding original variant data for this product
  const variantsData = switchItems[index].json.variants;

  // Build variants array for productVariantsBulkCreate
  // Match each variant with its corresponding optionValue ID
  const variantsForCreate = variantsData.map(variant => {
    // Find the matching option value ID for this size
    const optionValue = sizeOption.optionValues.find(ov => ov.name === variant.size);

    if (!optionValue) {
      throw new Error(`Option value not found for size: ${variant.size}`);
    }

    return {
      optionValues: [
        {
          optionId: sizeOption.id,
          id: optionValue.id
        }
      ],
      price: String(variant.price),
      inventoryItem: {
        sku: variant.fullSKU,
        inventory: variant.inventory,
        size: variant.size,
        image: variant.image,
        tracked: true
      }
    };
  });

  return {
    json: {
      productId: productId,
      productTitle: productData.title,
      mainfirstVariant: mainVariant,
      mediaID: mediaId,

      variants: variantsForCreate,
      originalVariantsData: variantsData
    }
  };
});

return output;
