// Input: item.json.{text, words, duration} del nodo Whisper
return items.map(item => {
  const dur = Number(item.json.duration || 0);

  // Redondea a 3 decimales y mantiene tipo number
  const round3 = (n) => Math.round(Number(n) * 1000) / 1000;

  const wordsLLM = (item.json.words || []).map(w => ({
    w: w.word,
    s: round3(w.start),
    e: round3(w.end),
  }));

  item.json.video_duration = round3(dur);
  item.json.words_llm = wordsLLM;      // array limpio para la IA
  item.json.text_llm  = item.json.text; // texto tal cual (o l√≠mpialo si quieres)

  return item;
});

// Get Gemini response
const data = $input.first().json;
const formData = $('Form: Upload Video').first();

// --- Config opcional ---
const VIDEO_DURATION_SECONDS =
  Number(formData?.json?.videoDurationSeconds) ||
  Number(formData?.json?.duration) ||
  Number(formData?.json?.meta?.durationSec) ||
  null;

// --- Helpers ---
const toMs = (v) => (isFinite(Number(v)) ? Math.round(Number(v) * 1000) : 0);

const stripCodeFences = (txt) =>
  String(txt || '')
    .replace(/```json\s*/gi, '')
    .replace(/```\s*/g, '')
    .trim();

const extractJsonObject = (txt) => {
  // 1) intenta parse directo
  try { return JSON.parse(txt); } catch {}
  // 2) intenta localizar el primer {...}
  const m = String(txt).match(/\{[\s\S]*\}/);
  if (m) {
    try { return JSON.parse(m[0]); } catch {}
  }
  throw new Error('No se pudo extraer JSON v√°lido del texto:\n' + String(txt).slice(0, 300));
};

const pickText = (payload) => {
  // Nuevos formatos (string en 'output')
  if (typeof payload === 'string') return payload;
  if (payload?.output) return payload.output;

  // Antiguos formatos (content.parts[0].text)
  if (Array.isArray(payload) && payload[0]?.output) return payload[0].output;
  if (Array.isArray(payload) && payload[0]?.content?.parts?.[0]?.text) return payload[0].content.parts[0].text;
  if (payload?.content?.parts?.[0]?.text) return payload.content.parts[0].text;

  // Otros
  if (payload?.response) return payload.response;
  if (payload?.text) return payload.text;

  // √öltimo recurso: stringify
  return JSON.stringify(payload);
};

// --- 1) Extrae texto crudo del output ---
let textResponse = pickText(data);

// --- 2) Limpia fences y parsea el JSON interior ---
textResponse = stripCodeFences(textResponse);
let parsed = extractJsonObject(textResponse);

// Si vino envuelto otra vez dentro de 'output', vuelve a parsear
if (!parsed?.shorts && typeof parsed?.output === 'string') {
  parsed = extractJsonObject(stripCodeFences(parsed.output));
}

const shorts = Array.isArray(parsed?.shorts) ? parsed.shorts : [];
if (!shorts.length) {
  throw new Error('Gemini no encontr√≥ segmentos interesantes');
}

// --- 3) Normalizaci√≥n opcional (si Gemini mand√≥ fracciones 0‚Äì1 y conocemos D) ---
const MIN = 15, MAX = 60;
const clamp = (v, lo, hi) => Math.max(lo, Math.min(hi, v));

const vf = "scale='min(1080,iw)':'min(1920,ih)':force_original_aspect_ratio=increase,crop=1080:1920,setsar=1";

return shorts.map((s, i) => {
  let startSec = Number(s.start);
  let endSec   = Number(s.end);

  if (!isFinite(startSec) || !isFinite(endSec)) {
    throw new Error(`Timestamps inv√°lidos en short #${i + 1}`);
  }

  // Si parecen porcentajes 0‚Äì1 y tenemos duraci√≥n del v√≠deo, convi√©rtelo a segundos
  if (VIDEO_DURATION_SECONDS && endSec <= 1.001) {
    startSec *= VIDEO_DURATION_SECONDS;
    endSec   *= VIDEO_DURATION_SECONDS;
  }

  // Fuerza rango 15‚Äì60s de forma suave
  let duration = endSec - startSec;
  if (duration < MIN) endSec = startSec + MIN;
  if (duration > MAX) endSec = startSec + MAX;
  duration = endSec - startSec;

  // Mant√©n dentro de la duraci√≥n del v√≠deo (si la sabemos)
  if (VIDEO_DURATION_SECONDS) {
    startSec = clamp(startSec, 0, Math.max(0, VIDEO_DURATION_SECONDS - MIN));
    endSec   = clamp(endSec, startSec + MIN, VIDEO_DURATION_SECONDS);
    duration = endSec - startSec;
  }

  // Redondeos
  const start = Number(startSec.toFixed(3));
  const end   = Number(endSec.toFixed(3));
  const dur   = Number(duration.toFixed(3));

  // Construye el comando FFmpeg completo (usa -t = duraci√≥n)
  const ffmpegCmd =
    `ffmpeg -y -hide_banner -loglevel error ` +
    `-ss ${start} -t ${dur} -i {input} ` +
    `-c:v h264_nvenc -cq 22 -vf "${vf}" ` +
    `-c:a aac -b:a 128k -ar 48000 -ac 2 -movflags +faststart {output}`;

  return {
    json: {
      index: i + 1,
      startTime: start,
      endTime: end,
      duration: dur,
      startTimeMs: toMs(start),
      endTimeMs: toMs(end),
      durationMs: toMs(dur),
      video_description_for_tiktok: s.video_description_for_tiktok ?? '',
      video_description_for_instagram: s.video_description_for_instagram ?? '',
      video_title_for_youtube_short: s.video_title_for_youtube_short ?? '',
      full_command: ffmpegCmd
    },
    binary: formData.binary
  };
});
