// Get the JSON output from the previous node, which is named "HTML: Extract Article Links & Titles"
const inputData = $('HTML: Extract Article Links & Titles').first().json;

// Get the arrays from that data
const urls = inputData.url;
const titles = inputData.title;

const pairedItems = [];

// Loop through the URLs and pair each one with its title
for (let i = 0; i < urls.length; i++) {
  pairedItems.push({
    json: {
      url: urls[i],
      title: titles[i]
    }
  });
}

// Return the new list of paired items
return pairedItems;
