let text =  $input.first().json.text;
const output = [];

// Remove ```json ... ``` or ``` ... ``` wrappers
text = text
      .replace(/```json\s*/gi, '')
      .replace(/```/g, '')
      .trim();

// Parse the cleaned JSON text
const parsed = JSON.parse(text);
output.push({ json: parsed });

return output;

