// The 'items' variable is an array of objects, where each object
// corresponds to an input item and has a 'json' property containing its data.
// In the Function node, items[0] will be the first input (e.g., from Google Sheets Get Row 1)
// and items[1] will be the second input (e.g., from Google Sheets Get Row 2) if you connect two nodes.

const list1 = $input.all(); // Assuming your first input is a list of maps/items under a 'data' key
const list2 = $('Get row(s) for Compare').all(); // Assuming your second input is a list of maps/items under a 'data' key

// ----------------------------------------------------------------------
// 1. Create a Set of all 'Name' values from the second list for quick lookup
// ----------------------------------------------------------------------
const namesInList2 = new Set(list2.map(item => item.json.Name).filter(name => name != null));

// ----------------------------------------------------------------------
// 2. Check if ANY name from list1 is missing in list2
//    The .some() method will stop and return true the moment the condition is met.
// ----------------------------------------------------------------------
const isMissingName = list1.some(item1 => {
    const nameToCheck = item1.json.Name;

    // Condition: Is the name NOT null AND is it NOT present in the namesInList2 Set?
    return nameToCheck != null && !namesInList2.has(nameToCheck);
});

// ----------------------------------------------------------------------
// 3. Output a SINGLE item with the Alert status
// ----------------------------------------------------------------------
if (isMissingName) {
    return [{
        json: {
            "Alert": true,
            "Message": "One or more names from the first list are missing in the second list."
        }
    }];
} else {
    return [{
        json: {
            "Alert": false,
            "Message": "All names from the first list are present in the second list."
        }
    }];
}

// Get the JSON string using the exact path provided by the user.
const jsonString = $input.first().json.output.string;

let parsedData;

// Check if the string exists before attempting to parse
if (!jsonString) {
    // Return an empty array or throw an error if no string is found
    // Throwing an error is usually better to stop the workflow if data is missing.
    throw new Error("Input string is empty or missing at the specified path: $input.first().json.output.string");
}

try {
    // 1. Parse the JSON string into a JavaScript array of objects
    parsedData = JSON.parse(jsonString);
} catch (error) {
    // Handle JSON parsing errors (e.g., if the string is malformed)
    throw new Error(`Failed to parse JSON string: ${error.message}`);
}

// 2. Ensure the parsed data is an array
if (!Array.isArray(parsedData)) {
    throw new Error('Parsed data is not an array. It cannot be split into multiple items.');
}

// 3. Map the array of objects into the n8n item format { json: object }
// Each element in this array will be treated as a new item by n8n, achieving the split.
const outputItems = parsedData.map(item => ({
    json: item,
}));

// 4. Return the new array of items
return outputItems;
