  <script>
    // === Modal Functions ===
    function openModal() {
      document.getElementById('infoModal').classList.add('active');
      document.body.style.overflow = 'hidden';
    }

    function closeModal(event) {
      if (!event || event.target.id === 'infoModal') {
        document.getElementById('infoModal').classList.remove('active');
        document.body.style.overflow = 'auto';
      }
    }

    // Close modal with Escape key
    document.addEventListener('keydown', function(e) {
      if (e.key === 'Escape') {
        const modal = document.getElementById('infoModal');
        if (modal && modal.classList.contains('active')) {
          closeModal();
        }
      }
    });

    // === Theme Toggle ===
    function toggleTheme() {
      document.body.classList.toggle('dark');
      const isDark = document.body.classList.contains('dark');
      localStorage.setItem('theme', isDark ? 'dark' : 'light');
    }

    // Restore theme on page load
    if (localStorage.getItem('theme') === 'light') {
      document.body.classList.remove('dark');
    }

    // === UNIFIED WEBHOOK URL ===
    // TODO: Replace this URL with your n8n webhook endpoint
    // All agents will send to the same URL with different agent_type
    const WEBHOOK_URL = 'https://primary-production-e46f.up.railway.app/webhook/webhook-endpoint';

    // === Send Message Function ===
    async function sendMessage() {
      const messageInput = document.getElementById('messageInput');
      const message = messageInput.value.trim();

      if (!message) {
        alert('Please enter a message before sending.');
        return;
      }

      // Show loading state
      showLoading('general');

      try {
        const response = await fetch(WEBHOOK_URL, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            message: message,
            agent_type: 'general',
            timestamp: new Date().toISOString()
          })
        });

        if (!response.ok) {
          throw new Error('Failed to send message');
        }

        const result = await response.json();

        // Display the AI response in the interface
        displayResponse(result.response || result.message || result.output, 'general');

        // Clear input
        messageInput.value = '';

      } catch (error) {
        console.error('Error:', error);
        displayError('Error sending message. Please try again.');
      }
    }

    // === Send to Specific Agent ===
    async function sendToAgent(agentType) {
      const messageInput = document.getElementById('messageInput');
      const message = messageInput.value.trim();

      if (!message) {
        alert('Please enter a message before selecting an agent.');
        return;
      }

      // Show loading state
      showLoading(agentType);

      try {
        const response = await fetch(WEBHOOK_URL, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            message: message,
            agent_type: agentType,
            timestamp: new Date().toISOString()
          })
        });

        if (!response.ok) {
          throw new Error('Failed to send message');
        }

        const result = await response.json();

        // Display the AI response in the interface
        displayResponse(result.response || result.message || result.output, agentType);

        // Clear input
        messageInput.value = '';

      } catch (error) {
        console.error('Error:', error);
        displayError(\`Error with \${agentType} agent. Please try again.\`);
      }
    }

    // === Display Response Function ===
    function displayResponse(responseText, agentType) {
      const responseArea = document.getElementById('responseArea');
      const responseBadge = document.getElementById('responseBadge');
      const responseContent = document.getElementById('responseContent');

      // Agent names and badge classes
      const agentInfo = {
        'database': { name: 'Database Search Agent', icon: 'fa-database', class: 'db-badge' },
        'web': { name: 'Web Search Agent', icon: 'fa-globe', class: 'web-badge' },
        'rag': { name: 'RAG Knowledge Agent', icon: 'fa-book', class: 'rag-badge' },
        'general': { name: 'AI Assistant', icon: 'fa-robot', class: 'general-badge' }
      };

      const info = agentInfo[agentType] || agentInfo['general'];

      // Display agent badge
      responseBadge.innerHTML = \`
        <div class="response-agent-badge \${info.class}">
          <i class="fas \${info.icon}"></i>
          \${info.name}
        </div>
      \`;

      // Display response text
      responseContent.textContent = responseText;

      // Show response area with animation
      responseArea.style.display = 'block';

      // Scroll to response smoothly
      setTimeout(() => {
        responseArea.scrollIntoView({ behavior: 'smooth', block: 'nearest' });
      }, 100);
    }

    // === Show Loading Function ===
    function showLoading(agentType) {
      const responseArea = document.getElementById('responseArea');
      const responseBadge = document.getElementById('responseBadge');
      const responseContent = document.getElementById('responseContent');

      const agentInfo = {
        'database': { name: 'Database Search Agent', icon: 'fa-database', class: 'db-badge' },
        'web': { name: 'Web Search Agent', icon: 'fa-globe', class: 'web-badge' },
        'rag': { name: 'RAG Knowledge Agent', icon: 'fa-book', class: 'rag-badge' },
        'general': { name: 'AI Assistant', icon: 'fa-robot', class: 'general-badge' }
      };

      const info = agentInfo[agentType] || agentInfo['general'];

      responseBadge.innerHTML = \`
        <div class="response-agent-badge \${info.class}">
          <i class="fas \${info.icon}"></i>
          \${info.name}
        </div>
      \`;

      responseContent.innerHTML = \`
        <div style="display: flex; align-items: center; gap: 10px; color: var(--muted);">
          <div class="loading-spinner"></div>
          Processing your request...
        </div>
      \`;

      responseArea.style.display = 'block';

      setTimeout(() => {
        responseArea.scrollIntoView({ behavior: 'smooth', block: 'nearest' });
      }, 100);
    }

    // === Display Error Function ===
    function displayError(errorMessage) {
      const responseArea = document.getElementById('responseArea');
      const responseBadge = document.getElementById('responseBadge');
      const responseContent = document.getElementById('responseContent');

      responseBadge.innerHTML = \`
        <div class="response-agent-badge" style="background: rgba(239, 68, 68, 0.1); color: #ef4444;">
          <i class="fas fa-exclamation-triangle"></i>
          Error
        </div>
      \`;

      responseContent.textContent = errorMessage;
      responseArea.style.display = 'block';

      setTimeout(() => {
        responseArea.scrollIntoView({ behavior: 'smooth', block: 'nearest' });
      }, 100);
    }

    // === Clear Response Function ===
    function clearResponse() {
      const responseArea = document.getElementById('responseArea');
      responseArea.style.display = 'none';
    }

    // === Enter key to send ===
    document.getElementById('messageInput').addEventListener('keydown', function(e) {
      if (e.key === 'Enter' && !e.shiftKey) {
        e.preventDefault();
        sendMessage();
