// Get email data
const email = $input.item.json;
const binary = $input.item.binary;

// Check if we have binary attachments
if (!binary || Object.keys(binary).length === 0) {
  return { 
    json: { 
      error: 'No invoice attachment found',
      emailId: email.id,
      emailSubject: email.subject
    } 
  };
}

// Find first PDF or image attachment
let invoiceFile = null;
let invoiceKey = null;

for (const key in binary) {
  const file = binary[key];
  if (file.mimeType?.includes('pdf') || 
      file.mimeType?.includes('image') ||
      file.mimeType?.includes('png') ||
      file.mimeType?.includes('jpg')) {
    invoiceFile = file;
    invoiceKey = key;
    break;
  }
}

if (!invoiceFile) {
  return { 
    json: { 
      error: 'No PDF or image attachment found',
      emailId: email.id,
      emailSubject: email.subject
    } 
  };
}

// The binary data in n8n is already base64 encoded
// Just use it directly without re-encoding
let base64Data = invoiceFile.data;

// If it's a Buffer, convert to base64 string
if (Buffer.isBuffer(base64Data)) {
  base64Data = base64Data.toString('base64');
}

// Build the data URL for OCR.space
const dataUrl = `data:${invoiceFile.mimeType};base64,${base64Data}`;

return {
  json: {
    emailId: email.id,
    emailSubject: email.subject,
    emailFrom: email.from,
    emailDate: email.date,
    attachmentData: base64Data,
    attachmentName: invoiceFile.fileName,
    mimeType: invoiceFile.mimeType,
    dataUrl: dataUrl
  }
};

